import { crawlFromApi, copyConfigApi } from '@/api/product';
export default {
  name: 'taoBao',
  data: function data() {
    return {
      soure_link: '',
      spinShow: false,
      grid: {
        xl: 8,
        lg: 8,
        md: 12,
        sm: 24,
        xs: 24
      },
      grid2: {
        xl: 12,
        lg: 12,
        md: 12,
        sm: 24,
        xs: 24
      },
      copyConfig: {
        'copy_type': '',
        'copy_num': 0
      },
      artFrom: {
        type: 'taobao',
        url: ''
      }
    };
  },
  computed: {},
  created: function created() {},
  mounted: function mounted() {
    this.getCopyConfig();
