          type: val
        }
      });
    },
    getCopyConfig: function getCopyConfig() {
      var _this = this;

      copyConfigApi().then(function (res) {
        _this.copyConfig.copy_type = res.data.copy_type;
        _this.copyConfig.copy_num = res.data.copy_num;
      });
    },
    // 生成表单
    add: function add() {
      var _this2 = this;

      if (this.soure_link) {
        var reg = /(http|ftp|https):\/\/[\w\-_]+(\.[\w\-_]+)+([\w\-\.,@?^=%&:/~\+#]*[\w\-\@?^=%&/~\+#])?/;

        if (!reg.test(this.soure_link)) {
          return this.$Message.warning('请输入以http开头的地址！');
        }

        this.spinShow = true;
        this.artFrom.url = this.soure_link;
        crawlFromApi(this.artFrom).then(function (res) {
          var info = res.data.productInfo;

          _this2.$emit('on-close', info);

          _this2.spinShow = false;
        }).catch(function (res) {
          _this2.spinShow = false;

          _this2.$Message.error(res.msg);
        });
      } else {
        this.$Message.warning('请输入链接地址！');
      }
    }
  }
};
