import { speeChcraft, serviceCate, addServiceCate, addSpeeChcraft, serviceCateUpdate, editServiceCate } from '@/api/kefu';
export default {
  name: 'words',
  props: {
    isWords: {
      type: Boolean,
      default: false
    }
  },
  computed: {
    cateStatus: function cateStatus() {
      if (this.cateData.name && this.cateData.sort !== '') {
        return false;
      } else {
        return true;
      }
    },
    msgStatus: function msgStatus() {
      if (this.msgData.message) {
        return false;
      } else {
        return true;
      }
    }
  },
  data: function data() {
    return {
      isWordShow: false,
      // 编辑窗
      wordsList: [],
      wordsTab: [{
        title: '个人库',
        key: 1
      }, {
        title: '公共库',
        key: 0
      }],
      wordsTabCur: 1,
      wordsData: {
        isScroll: true,
        ops: {
          vuescroll: {
            mode: 'slide',
            enable: false,
            tips: {
              deactive: 'Push to Load',
              active: 'Release to Load',
              start: 'Loading...',
              beforeDeactive: 'Load Successfully!'
            },
            auto: false,
            autoLoadDistance: 0,
            pullRefresh: {
              enable: false
            },
            pushLoad: {
              enable: true,
              auto: true,
              autoLoadDistance: 10
            }
          },
          bar: {
            background: '#393232',
            opacity: '.5',
            size: '2px'
          }
        },
        page: 1,
        limit: 10,
        searchTxt: '',
        cate: [],
        // 分类
        cateId: '' // 分类id

      },
      // 分组数据
      cateData: {
        status: 0,
        // 0 新增 1编辑
        name: '',
        sort: '',
        isCate: false,
        // 分组状态开关
        id: ''
      },
      // 编辑分组列表
      editList: {
        status: false
      },
      // 话术添加数据
      msgData: {
        isCateMeg: false,
        msgCateId: '',
        message: '',
        title: '',
        status: 0,
        // 0 新增 1修改
        editId: ''
      },
      selectData: ''
    };
  },
  mounted: function mounted() {
    Promise.all([this.getServiceCate()]);
  },
  methods: {
    // 关闭添加话术弹窗
    closeMsgBox: function closeMsgBox() {
      this.msgData.isCateMeg = false;
    },
    // 选择话术
    selectWords: function selectWords(item) {
      this.$emit('selectMsg', item.message);
    },
    // 关闭弹窗
    closeBox: function closeBox() {
      this.$emit('closeBox');
    },
    // 搜索
    bindSearch: function bindSearch() {
      this.wordsData.page = 1;
      this.wordsData.isScroll = true;
      this.wordsList = [];
      this.getWordsList();
    },
    // 顶部切换
    bindTab: function bindTab(item) {
      this.wordsTabCur = item.key;
      this.wordsData.isScroll = true;
      this.wordsData.page = 1;
      this.wordsData.cate = [];
      this.wordsList = [];
      this.getServiceCate();
    },
    // 选择话术分类
    changeCate: function changeCate(item) {
      this.wordsData.isScroll = true;
      this.wordsList = [];
      this.wordsData.page = 1;
      this.wordsData.cateId = item.id;
      this.msgData.msgCateId = item.id;
      this.getWordsList();
    },
    // 获取话术分类
    getServiceCate: function getServiceCate() {
      var _this = this;

      var data = {
        id: '',
        name: '全部'
      };
      serviceCate({
        type: this.wordsTabCur
      }).then(function (res) {
        var tempArr = JSON.parse(JSON.stringify(res.data.data));
        res.data.data.unshift(data);
        _this.wordsData.cateId = res.data.data.length ? res.data.data[0].id : '';
        _this.msgData.msgCateId = _this.wordsData.cateId;
        _this.wordsData.cate = res.data.data;
        _this.selectData = tempArr;

        _this.getWordsList();
      });
    },
    // 话术滚动到底部
    handleWordsScroll: function handleWordsScroll(vm, refreshDom, done) {
      this.getWordsList();
      done();
    },
    // 常用语
    getWordsList: function getWordsList() {
      var _this2 = this;

      speeChcraft({
        page: this.wordsData.page,
        limit: this.wordsData.limit,
        title: this.wordsData.searchTxt,
        cate_id: this.wordsData.cateId,
        type: this.wordsTabCur
      }).then(function (res) {
        _this2.wordsData.isScroll = res.data.length >= _this2.wordsData.limit;
        _this2.wordsList = _this2.wordsList.concat(res.data);
        _this2.wordsData.page++;

        _this2.$nextTick(function () {
          if (_this2.wordsList.length > 0) {
            _this2.$refs['scrollBox'].refresh();
          }
        });
      });
    },
    // 打开分组弹窗
    openCate: function openCate(key, item) {
      this.cateData.status = key;
      this.cateData.isCate = true;

      if (key == 1) {
        this.cateData.name = item.name;
        this.cateData.id = item.id;
        this.cateData.sort = item.sort;
      }
    },
    // 关闭分组弹窗
    closeCate: function closeCate() {
      this.cateData.isCate = false;
      this.cateData.name = '';
      this.cateData.sort = '';
    },
    // 分组添加
    cateConfirm: function cateConfirm() {
      var _this3 = this;

      if (!this.cateData.status) {
        addServiceCate({
          name: this.cateData.name,
          sort: this.cateData.sort
        }).then(function (res) {
          _this3.cateData.isCate = false;
          _this3.cateData.name = '';
          _this3.cateData.sort = '';
          _this3.page = 1;
          _this3.wordsData.isScroll = true;
          _this3.wordsList = [];

          _this3.$Message.success(res.msg);

          _this3.getServiceCate();
        }).catch(function (error) {
          _this3.$Message.error(error.msg);
        });
      } else {
        editServiceCate(this.cateData.id, {
          name: this.cateData.name,
          sort: this.cateData.sort
        }).then(function (res) {
          _this3.cateData.isCate = false;
          _this3.cateData.name = '';
          _this3.cateData.sort = '';
          _this3.cateData.id = '';
          _this3.page = 1;
          _this3.wordsData.isScroll = true;
          _this3.wordsList = [];

          _this3.$Message.success(res.msg);

          _this3.getServiceCate();
        });
      }
    },
    // 话术打开
    addMsg: function addMsg() {
      this.msgData.isCateMeg = true;
      this.msgData.status = 0;
    },
    // 话术添加
    msgConfirm: function msgConfirm() {
      var _this4 = this;

      if (!this.msgData.status) {
        addSpeeChcraft({
          title: this.msgData.title,
          cate_id: this.msgData.msgCateId,
          message: this.msgData.message
        }).then(function (res) {
          _this4.msgData.isCateMeg = false;
          _this4.msgData.title = '';
          _this4.msgData.message = '';
          _this4.msgData.msgCateId = _this4.wordsData.cateId;

          _this4.$Message.success(res.msg);

          _this4.wordsData.isScroll = true;
          _this4.wordsData.page = 1;
          _this4.wordsList = [];

          _this4.getWordsList();
        }).catch(function (error) {
          _this4.$Message.error(error.msg);
        });
      } else {
        serviceCateUpdate(this.msgData.editId, {
          title: this.msgData.title,
          cate_id: this.msgData.msgCateId,
          message: this.msgData.message
        }).then(function (res) {
          _this4.msgData.isCateMeg = false;
          _this4.msgData.title = '';
          _this4.msgData.message = '';
          _this4.msgData.msgCateId = _this4.wordsData.cateId;

          _this4.$Message.success(res.msg);

          _this4.wordsData.isScroll = true;
          _this4.wordsData.page = 1;
          _this4.wordsList = [];

          _this4.getWordsList();
        });
      }
    },
    // 编辑话术
    bindEdit: function bindEdit(item) {
      this.msgData.status = 1;
      this.msgData.isCateMeg = true;
      this.msgData.message = item.message;
      this.msgData.title = item.title;
      this.msgData.editId = item.id;
    },
    // 删除话术
    delMsg: function delMsg(row, tit, num) {
      var _this5 = this;

      var delfromData = {
        title: tit,
        num: num,
        url: "/service/speechcraft/".concat(row.id),
        method: 'DELETE',
        ids: '',
        kefu: true
      };
      this.$modalSure(delfromData).then(function (res) {
        _this5.wordsList.splice(num, 1);

        _this5.$Message.success(res.msg);
      }).catch(function (res) {
        _this5.$Message.error(res.msg);
      });
    },
    delCate: function delCate(row, tit, num) {
      var _this6 = this;

      var delfromData = {
        title: tit,
        num: num,
        url: "/service/cate/".concat(row.id),
        method: 'DELETE',
        ids: '',
        kefu: true
      };
      this.$modalSure(delfromData).then(function (res) {
        _this6.wordsData.cate.splice(num, 1);

        _this6.page = 1;
        _this6.wordsData.isScroll = true;
        _this6.wordsList = [];

        _this6.$Message.success(res.msg);

        _this6.getServiceCate();
      }).catch(function (res) {
        _this6.$Message.error(res.msg);
      });
    }
  }
};
