import { getUserLabel, putUserLabel, putUsersSetLabel } from '@/api/user';
export default {
  name: "userLabel",
  props: {
    uid: {
      type: String | Number | Object,
      default: ''
    }
  },
  data: function data() {
    return {
      isUser: false,
      labelList: [],
      activeIds: [],
      info: {},
      oid: ''
    };
  },
  watch: {
    uid: {
      handler: function handler(nVal, oVal) {
        if (nVal != oVal) {
          this.getList();
        }
      },
      deep: true
    }
  },
  mounted: function mounted() {
    this.getList();
  },
  methods: {
    getList: function getList() {
      var _this = this;

      var uid = 0;

      if (this.uid instanceof Object) {
        this.info = this.uid;

        if (!this.uid.all && this.uid.uids.length > 1 || this.uid.all) {
          uid = 0;
        } else {
          uid = this.uid.uids[0];
        }
      } else {
        uid = this.uid;
      }

      this.oid = uid;
      getUserLabel(uid).then(function (res) {
        res.data.map(function (el) {
          _this.isUser = true;
          el.label.map(function (label) {
            if (label.disabled) {
              _this.activeIds.push(label.id);
            }
          });
        });
        _this.labelList = res.data;
      });
    },
    selectLabel: function selectLabel(label) {
      if (label.disabled) {
        var index = this.activeIds.indexOf(label.id);
        this.activeIds.splice(index, 1);
        label.disabled = false;
      } else {
        this.activeIds.push(label.id);
        label.disabled = true;
      }
    },
    // 确定
    subBtn: function subBtn() {
      var _this2 = this;

      var unLaberids = [];
      this.labelList.map(function (item) {
        item.label.map(function (i) {
          if (i.disabled == false) {
            unLaberids.push(i.id);
          }
        });
      });
      var obj = this.info;
      obj.label_id = this.activeIds;
      (this.oid == 0 ? putUsersSetLabel(obj) : putUserLabel(this.oid, {
        label_ids: this.activeIds,
        un_label_ids: unLaberids
      })).then(function (res) {
        _this2.activeIds = [];
        _this2.labelList = [];

        _this2.$Message.success(res.msg);

        _this2.$emit('close', _this2.oid);
      }).catch(function (error) {
        _this2.$Message.error(error.msg);
      });
    },
    cancel: function cancel() {
      this.activeIds = [];
      this.labelList = [];
      this.$emit('close');
    }
  }
};
