import { productStoreLabel } from '@/api/product';
export default {
  name: "storeLabelList",
  props: {},
  data: function data() {
    return {
      labelList: [],
      dataLabel: [],
      isStore: false
    };
  },
  mounted: function mounted() {},
  methods: {
    inArray: function inArray(search, array) {
      for (var i in array) {
        if (array[i].id == search) {
          return true;
        }
      }

      return false;
    },
    // 用户标签
    storeLabel: function storeLabel(data) {
      var _this = this;

      this.dataLabel = data;
      productStoreLabel().then(function (res) {
        res.data.map(function (el) {
          if (el.children) {
            _this.isStore = true;
            el.children.map(function (label) {
              if (_this.inArray(label.id, _this.dataLabel)) {
                label.disabled = true;
              } else {
                label.disabled = false;
              }
            });
          }
        });
        _this.labelList = res.data;
      }).catch(function (res) {
        _this.$Message.error(res.msg);
      });
    },
    selectLabel: function selectLabel(label) {
      if (label.disabled) {
        var index = this.dataLabel.indexOf(this.dataLabel.filter(function (d) {
          return d.id == label.id;
        })[0]);
        this.dataLabel.splice(index, 1);
        label.disabled = false;
      } else {
        this.dataLabel.push({
          'label_name': label.label_name,
          'id': label.id
        });
        label.disabled = true;
      }
    },
    // 确定
    subBtn: function subBtn() {
      this.$emit('activeData', JSON.parse(JSON.stringify(this.dataLabel)));
    },
    cancel: function cancel() {
      this.$emit('close');
    }
  }
};
