import vuedraggable from 'vuedraggable';
import goodsList from '@/components/goodsList';
export default {
  name: 'c_goods',
  props: {
    configObj: {
      type: Object
    }
  },
  components: {
    goodsList: goodsList,
    draggable: vuedraggable
  },
  watch: {
    configObj: {
      handler: function handler(nVal, oVal) {
        this.defaults = nVal;
      },
      immediate: true,
      deep: true
    }
  },
  data: function data() {
    return {
      modals: false,
      goodsList: [],
      tempGoods: {},
      defaults: {}
    };
  },
  created: function created() {
    this.defaults = this.configObj;
  },
  methods: {
    //对象数组去重；
    unique: function unique(arr) {
      var res = new Map();
      return arr.filter(function (arr) {
        return !res.has(arr.id) && res.set(arr.id, 1);
      });
    },
    getProductId: function getProductId(data) {
      // this.tempGoods = data
      this.modals = false;
      var list = this.defaults.goodsList.list.concat(data);
      this.defaults.goodsList.list = this.unique(list);
    },
    cancel: function cancel() {
      this.modals = false; // this.tempGoods = {}
    },
    ok: function ok() {
      this.defaults.goodsList.list.push(this.tempGoods);
    },
    bindDelete: function bindDelete(index) {
      this.defaults.goodsList.list.splice(index, 1);
    }
  }
};
