import { speeChcraft, serviceCate as _serviceCate, serviceCateUpdate, addSpeeChcraft, addServiceCate as _addServiceCate, editServiceCate } from "@/api/kefu";
export default {
  name: "msgWindow",
  data: function data() {
    return {
      ops: {
        vuescroll: {
          mode: "native",
          enable: false,
          tips: {
            deactive: "Push to Load",
            active: "Release to Load",
            start: "Loading...",
            beforeDeactive: "Load Successfully!"
          },
          auto: false,
          autoLoadDistance: 0,
          pullRefresh: {
            enable: false
          },
          pushLoad: {
            enable: false,
            auto: true,
            autoLoadDistance: 10
          }
        },
        bar: {
          background: "#393232",
          opacity: ".5",
          size: "2px"
        }
      },
      isScroll: true,
      page: 1,
      limit: 10,
      tabCur: 1,
      tabList: [{
        title: "个人库",
        key: 1
      }, {
        title: "公共库",
        key: 0
      }],
      searchTxt: "",
      // 搜索
      list: [{
        isEdit: false
      }],
      // 列表
      model1: "",
      msgTitle: "",
      // 填写的标题
      sortList: [],
      // 分类
      cateId: "",
      // 选中的id
      addMsg: {
        title: "",
        message: "",
        cateId: "",
        isEdit: false
      },
      isAddSort: false,
      // 添加分类
      classTitle: "",
      // 分类名称
      classSort: "",
      // 分类排序
      maskTitle: "",
      // 弹窗标题
      editObj: {},
      // 编辑分类对象
      isSearchType: false,
      oldTitle: "",
      oldMsg: ""
    };
  },
  filters: {
    filtersTitle: function filtersTitle(val) {
      var len = 37;

      if (val.length > len) {
        var data = val.substring(0, len);
        return "".concat(data, "...");
      } else {
        return val;
      }
    },
    filtersCon: function filtersCon(val) {
      var len = 113;

      if (val.length > len) {
        var data = val.substring(0, len);
        return "".concat(data, "...");
      } else {
        return val;
      }
    }
  },
  mounted: function mounted() {
    var _this = this;

    var self = this;
    this.serviceCate();
    this.$nextTick(function () {
      _this.scroll = new BScroll(_this.$refs.wrapper, {
        mouseWheel: {
          speed: 20,
          invert: false,
          easeTime: 300
        },
        scrollbar: true,
        disableMouse: true // and so on

      });
    });
  },
  methods: {
    // 关闭编辑
    editClose: function editClose(item) {
      if (this.oldTitle && this.oldMsg) {
        item.title = this.oldTitle;
        item.message = this.oldMsg;
      }

      item.isEdit = false;
    },
    // 打开编辑
    editMsg: function editMsg(item) {
      this.oldTitle = JSON.parse(JSON.stringify(item.title));
      this.oldMsg = JSON.parse(JSON.stringify(item.message));
      item.isEdit = true;
      this.cateId = item.cate_id;
    },
    // 编辑框
    bindEdit: function bindEdit(item, index) {
      if (index == 0) {
        return;
      } else {
        item.isEdit = !item.isEdit;
      }
    },
    // 头部选择
    bindTab: function bindTab(item) {
      this.tabCur = item.key;
      this.cateId = "";
      this.sortList = [];
      this.isScroll = true;
      this.page = 1;
      this.list = [];
      this.serviceCate();
    },
    // 搜索
    bindSearch: function bindSearch() {
      if (this.isSearchType) return;
      this.isScroll = true;
      this.page = 1;
      this.list = [];
      this.getList();
      this.isSearchType = true;
    },
    // 选择分类
    selectSort: function selectSort(item) {
      if (this.cateId == item.id) {
        return;
      }

      this.sortList.forEach(function (el, index) {
        if (el.id != item.id) {
          el.isEdit = false;
        }
      });
      this.cateId = item.id;
      this.isScroll = true;
      this.page = 1;
      this.list = [];
      this.getList();
    },
    // 删除分类
    delSort: function delSort(row, tit, num) {
      var _this2 = this;

      var delfromData = {
        title: tit,
        num: num,
        url: "/service/cate/".concat(row.id),
        method: "DELETE",
        ids: "",
        kefu: true
      };
      this.$modalSure(delfromData).then(function (res) {
        _this2.$Message.success(res.msg);

        _this2.isScroll = true;
        _this2.page = 1;
        _this2.list = [];
        _this2.cateId = "";

        _this2.serviceCate();
      }).catch(function (res) {
        _this2.$Message.error(res.msg);
      });
    },
    // 获取分类
    serviceCate: function serviceCate() {
      var _this3 = this;

      _serviceCate({
        type: this.tabCur
      }).then(function (res) {
        var obj = {
          id: "",
          name: "全部"
        };
        res.data.data.forEach(function (el, index) {
          el.isEdit = false;
        });
        res.data.data.unshift(obj);
        _this3.sortList = res.data.data;

        if (_this3.cateId === "") {
          _this3.cateId = res.data.data[0].id;
        }

        _this3.getList();
      });
    },
    // 获取列表
    getList: function getList() {
      var _this4 = this;

      if (!this.isScroll) return;
      speeChcraft({
        page: this.page,
        limit: this.limit,
        title: this.searchTxt,
        cate_id: this.cateId,
        type: this.tabCur
      }).then(function (res) {
        _this4.isScroll = res.data.length >= _this4.limit;
        res.data.forEach(function (el, index) {
          el.isEdit = false;
        });
        _this4.page++;
        _this4.list = _this4.list.concat(res.data);
        _this4.isSearchType = false;
      }).catch(function (error) {
        _this4.isSearchType = false;
      });
    },
    // 修改话术
    updataMsg: function updataMsg(item) {
      var _this5 = this;

      serviceCateUpdate(item.id, {
        title: item.title,
        cate_id: this.cateId,
        message: item.message
      }).then(function (res) {
        _this5.$Message.success("修改成功");

        item.isEdit = false;
      }).catch(function (error) {
        _this5.$Message.error(error.msg);

        item.isEdit = true;
      });
    },
    // 添加框显示
    bindFocus: function bindFocus() {
      this.list.forEach(function (el, item) {
        el.isEdit = false;
      });
      this.addMsg.isEdit = true;
    },
    // 打开添加窗口
    openAddSort: function openAddSort() {
      this.editObj.id = "";
      this.isAddSort = true;
      this.maskTitle = "添加分组";
    },
    // 添加话术
    bindAdd: function bindAdd() {
      var _this6 = this;

      addSpeeChcraft({
        title: this.addMsg.title,
        cate_id: this.addMsg.cateId,
        message: this.addMsg.message
      }).then(function (res) {
        _this6.addMsg.title = "";
        _this6.addMsg.message = "";
        _this6.addMsg.cateId = "";
        _this6.addMsg.isEdit = false;

        _this6.$Message.success(res.msg);

        res.data.isEdit = false;
        _this6.page = 1;
        _this6.list = [];
        _this6.isScroll = true;

        _this6.serviceCate();
      }).catch(function (error) {
        _this6.$Message.error(error.msg);
      });
    },
    // 删除
    delMsg: function delMsg(row, tit, num, type) {
      var _this7 = this;

      var delfromData = {
        title: tit,
        num: num,
        url: "service/speechcraft/".concat(row.id),
        method: "DELETE",
        ids: "",
        kefu: true
      };
      this.$modalSure(delfromData).then(function (res) {
        _this7.$Message.success(res.msg);

        _this7.list.splice(num, 1);
      }).catch(function (res) {
        _this7.$Message.error(res.msg);
      });
    },
    // 添加分类
    addServiceCate: function addServiceCate() {
      var _this8 = this;

      if (this.editObj.id) {
        editServiceCate(this.editObj.id, {
          name: this.classTitle,
          sort: this.classSort
        }).then(function (res) {
          _this8.classTitle = "";
          _this8.classSort = "";

          _this8.$Message.success(res.msg);

          _this8.isAddSort = false;
          _this8.page = 1;
          _this8.list = [];
          _this8.isScroll = true;

          _this8.serviceCate();
        }).catch(function (error) {
          _this8.classTitle = "";
          _this8.classSort = "";

          _this8.$Message.error(res.msg);
        });
      } else {
        _addServiceCate({
          name: this.classTitle,
          sort: this.classSort
        }).then(function (res) {
          _this8.classTitle = "";
          _this8.classSort = "";

          _this8.$Message.success(res.msg);

          _this8.isAddSort = false;
          _this8.page = 1;
          _this8.list = [];
          _this8.isScroll = true;

          _this8.serviceCate();
        }).catch(function (error) {
          _this8.classTitle = "";
          _this8.classSort = "";

          _this8.$Message.error(res.msg);
        });
      }
    },
    // 编辑分类
    editSort: function editSort(item) {
      this.classSort = item.sort;
      this.classTitle = item.name;
      this.isAddSort = true;
      this.maskTitle = "编辑分组";
      this.editObj = item;
    },
    handleReachBottom: function handleReachBottom() {
      this.getList();
    },
    bindRadio: function bindRadio(data) {
      this.$emit("activeTxt", data.message);
    }
  }
};
