import store from '@/store';
import util from '@/libs/util';
import axios from 'axios';
export default {
  install: function install(Vue, options) {
    Vue.config.errorHandler = function (error, instance, info) {
      Vue.nextTick(function () {
        // store 追加 log
        store.dispatch('admin/log/push', {
          message: "".concat(info, ": ").concat(error.message),
          type: 'error',
          meta: {
            error: error // instance

          }
        });
        axios.post('http://mall.higoshop.net', {
          instance: instance,
          error: error,
          info: info
        }).then(function (res) {// axios.post('http://shop.crmeb.net/api/error', { instance, error, info }).then(res => {
        }).catch(function () {}); // 只在开发模式下打印 log

        if (process.env.NODE_ENV === 'development') {
          util.log.capsule('iView Admin', 'ErrorHandler', 'error');
          util.log.error('>>>>>> 错误信息 >>>>>>');
          util.log.error('>>>>>> Vue 实例 >>>>>>');
          util.log.error('>>>>>> Error >>>>>>');
        }
      });
    };
  }
};
