import { productInfo } from '@/api/kefu';
import { serviceInfo } from '@/api/kefu_mobile';
export default {
  name: "goods_detail",
  props: {
    goodsId: {
      type: String | Number,
      default: ''
    }
  },
  data: function data() {
    return {
      value2: 0,
      goodsInfo: {},
      goodsId: '',
      swiperOption: {
        //显示分页
        pagination: {
          el: '.swiper-pagination'
        },
        //设置点击箭头
        navigation: {
          nextEl: '.swiper-button-next',
          prevEl: '.swiper-button-prev'
        },
        //自动轮播
        autoplay: {
          delay: 2000,
          //当用户滑动图片后继续自动轮播
          disableOnInteraction: false
        },
        //开启循环模式
        loop: true
      }
    };
  },
  created: function created() {
    this.goodsId = this.$route.query.goodsId;
    serviceInfo().then(function (res) {
      window.document.title = "".concat(res.data.site_name, " - \u5546\u54C1\u8BE6\u60C5");
    });
  },
  mounted: function mounted() {
    this.getInfo();
  },
  methods: {
    getInfo: function getInfo() {
      var _this = this;

      productInfo(this.goodsId).then(function (res) {
        _this.goodsInfo = res.data;
      });
    }
  }
};
