import vuedraggable from 'vuedraggable';
import uploadPictures from '@/components/uploadPictures';
import linkaddress from '@/components/linkaddress';
export default {
  name: 'uploadPic',
  props: {
    listData: {
      type: Array
    },
    type: {
      type: Number
    }
  },
  components: {
    draggable: vuedraggable,
    uploadPictures: uploadPictures,
    linkaddress: linkaddress
  },
  data: function data() {
    return {
      modalPic: false,
      isChoice: '单选',
      gridBtn: {
        xl: 4,
        lg: 8,
        md: 8,
        sm: 8,
        xs: 8
      },
      gridPic: {
        xl: 6,
        lg: 8,
        md: 12,
        sm: 12,
        xs: 12
      },
      activeIndex: 0,
      lastObj: {
        name: '',
        pic: '',
        url: ''
      }
    };
  },
  mounted: function mounted() {},
  watch: {
    configObj: {
      handler: function handler(nVal, oVal) {},
      deep: true
    }
  },
  methods: {
    linkUrl: function linkUrl(e) {
      this.listData[this.activeIndex].url = e;
    },
    getLink: function getLink(index) {
      this.activeIndex = index;
      this.$refs.linkaddres.modals = true;
    },
    addBox: function addBox() {
      if (this.listData.length == 0) {
        this.listData.push(this.lastObj);
      } else {
        var obj = JSON.parse(JSON.stringify(this.listData[this.listData.length - 1]));
        obj.name = '';
        obj.pic = '';
        obj.url = '';
        this.listData.push(obj);
      } // this.$emit('parentFun',this.listData)

    },
    // 点击图文封面
    modalPicTap: function modalPicTap(title, index) {
      this.activeIndex = index;
      this.modalPic = true;
    },
    // 添加自定义弹窗
    addCustomDialog: function addCustomDialog(editorId) {
      window.UE.registerUI('test-dialog', function (editor, uiName) {
        var dialog = new window.UE.ui.Dialog({
          iframeUrl: '/admin/widget.images/index.html?fodder=dialog',
          editor: editor,
          name: uiName,
          title: '上传图片',
          cssRules: 'width:1200px;height:500px;padding:20px;'
        });
        this.dialog = dialog; // 参考上面的自定义按钮

        var btn = new window.UE.ui.Button({
          name: 'dialog-button',
          title: '上传图片',
          cssRules: "background-image: url(../../../assets/images/icons.png);background-position: -726px -77px;",
          onclick: function onclick() {
            // 渲染dialog
            dialog.render();
            dialog.open();
          }
        });
        return btn;
      }, 37);
    },
    // 获取图片信息
    getPic: function getPic(pc) {
      var _this = this;

      this.$nextTick(function () {
        _this.listData[_this.activeIndex].pic = pc.att_dir;
        _this.modalPic = false;
      });
    },
    // 删除
    bindDelete: function bindDelete(item, index) {
      if (this.listData.length == 1) {
        this.lastObj = this.listData[0];
      }

      this.listData.splice(index, 1); // this.$emit('parentFun',this.listData)
    }
  }
};
