import { orderInfo, refundInfo } from '@/api/kefu';
export default {
  name: "order_detail",
  props: {
    orderId: {
      type: String | Number,
      default: ''
    },
    isReturen: {
      type: Boolean,
      default: false
    }
  },
  data: function data() {
    return {
      orderDetail: {},
      orderList: [],
      columns1: [{
        title: '商品ID',
        slot: 'id',
        maxWidth: 80
      }, {
        title: '商品名称',
        slot: 'name',
        minWidth: 160
      }, {
        title: '商品分类',
        slot: 'className'
      }, {
        title: '商品售价',
        slot: 'price'
      }, {
        title: '商品数量',
        slot: 'total_num'
      }],
      customForm: []
    };
  },
  mounted: function mounted() {
    this.getOrderInfo();
  },
  methods: {
    getOrderInfo: function getOrderInfo() {
      var _this = this;

      (this.isReturen ? refundInfo(this.orderId) : orderInfo(this.orderId)).then(function (res) {
        res.data.orderInfo.add_time = _this.$moment(parseInt(res.data.orderInfo.add_time) * 1000).format('YYYY-MM-DD');
        _this.orderDetail = res.data;
        _this.orderList = res.data.orderInfo.cartInfo;
        var customForm = res.data.orderInfo.custom_form;
        var count = Math.ceil(customForm.length / 2);
        var customArray = new Array();

        for (var i = 0; i < count; i++) {
          var list = customForm.slice(i * 2, i * 2 + 2);
          if (list.length) customArray.push({
            list: list
          });
        }

        _this.customForm = customArray;
      });
    }
  }
};
