import { productGetTempKeysApi } from '@/api/product';
import '../../../public/UEditor/dialogs/internal';
export default {
  name: 'vide11o',
  data: function data() {
    return {
      upload: {
        videoIng: false // 是否显示进度条；

      },
      progress: 0,
      // 进度条默认0
      videoLink: '',
      formValidate: {
        video_link: ''
      }
    };
  },
  methods: {
    // 删除视频；
    delVideo: function delVideo() {
      var that = this;
      that.$set(that.formValidate, 'video_link', '');
    },
    zh_uploadFile: function zh_uploadFile() {
      if (this.videoLink) {
        this.formValidate.video_link = this.videoLink;
      } else {
        this.$refs.refid.click();
      }
    },
    zh_uploadFile_change: function zh_uploadFile_change(evfile) {
      var that = this;
      var suffix = evfile.target.files[0].name.substr(evfile.target.files[0].name.indexOf('.'));

      if (suffix !== '.mp4') {
        return that.$Message.error('只能上传MP4文件');
      }

      productGetTempKeysApi().then(function (res) {
        that.$videoCloud.videoUpload({
          type: res.data.type,
          evfile: evfile,
          res: res,
          uploading: function uploading(status, progress) {
            that.upload.videoIng = status;
          }
        }).then(function (res) {
          that.formValidate.video_link = res.url;
          that.$Message.success('视频上传成功');
        }).catch(function (res) {
          that.$Message.error(res);
        });
      });
    },
    uploads: function uploads() {
      nowEditor.editor.execCommand('inserthtml', "<p><img/><video src='".concat(this.formValidate.video_link, "' controls></video><br/></p>"));
      nowEditor.dialog.close(true);
    }
  }
};
