import Setting from '@/setting';
import ClipboardJS from "clipboard";
import { diyList, diyDel, setStatus as _setStatus } from '@/api/diy';
import { mapState } from 'vuex';
export default {
  name: 'devise_list',
  data: function data() {
    return {
      grid: {
        xl: 7,
        lg: 7,
        md: 12,
        sm: 24,
        xs: 24
      },
      loading: false,
      columns1: [{
        title: '页面ID',
        key: 'id',
        minWidth: 120
      }, {
        title: '专题页名称',
        key: 'title',
        minWidth: 120
      }, {
        title: '专题链接',
        slot: 'link',
        minWidth: 300
      }, {
        title: '添加时间',
        key: 'add_time',
        minWidth: 120
      }, {
        title: '更新时间',
        key: 'update_time',
        minWidth: 120
      }, {
        title: '操作',
        slot: 'action',
        fixed: 'right',
        minWidth: 300
      }],
      list: [],
      total: 0,
      page: 1,
      limit: 15,
      BaseURL: Setting.apiBaseURL.replace(/adminapi/, "")
    };
  },
  created: function created() {
    this.getList();
  },
  mounted: function mounted() {
    this.$nextTick(function () {
      var _this = this;

      var clipboard = new ClipboardJS('.copy-data');
      clipboard.on("success", function () {
        _this.$Message.success('复制预览链接成功');
      });
    });
  },
  methods: {
    // 获取列表
    getList: function getList() {
      var _this2 = this;

      this.loading = true;
      diyList({
        type: 2,
        page: this.page,
        limit: this.limit
      }).then(function (res) {
        _this2.loading = false;
        _this2.list = res.data.list;
        _this2.total = res.data.count;
      });
    },
    // 编辑
    edit: function edit(row) {
      this.$router.push({
        path: '/admin/pages/special/diy',
        query: {
          id: row.id,
          name: row.name,
          type: row.type
        }
      });
    },
    // 添加
    add: function add() {
      this.$router.push({
        path: '/admin/pages/special/diy',
        query: {
          id: 0,
          name: '首页',
          type: 2
        }
      });
    },
    // 删除
    del: function del(row) {
      var _this3 = this;

      var delfromData = {
        title: "删除",
        num: 2000,
        url: "diy/del/" + row.id,
        method: "DELETE",
        data: {
          type: 2
        }
      };
      this.$modalSure(delfromData).then(function (res) {
        _this3.getList();
      }).catch(function (res) {
        _this3.$Message.error(res.msg);
      });
    },
    // 使用模板
    setStatus: function setStatus(row) {
      var _this4 = this;

      _setStatus(row.id).then(function (res) {
        _this4.$Message.success(res.msg);

        _this4.getList();
      });
    },
    pageChange: function pageChange(index) {
      this.page = index;
      this.getList();
    }
  }
};
