import { mapState } from "vuex";
import uploadPictures from '@/components/uploadPictures';
export default {
  name: "pageTitle",
  components: {
    uploadPictures: uploadPictures
  },
  data: function data() {
    return {
      value: '',
      name: '',
      isShow: true,
      picList: ['icondantu', 'iconpingpu', 'iconlashen'],
      bgColor: false,
      bgPic: false,
      tabVal: 0,
      colorPicker: '#f5f5f5',
      modalPic: false,
      isChoice: '单选',
      gridBtn: {
        xl: 4,
        lg: 8,
        md: 8,
        sm: 8,
        xs: 8
      },
      gridPic: {
        xl: 6,
        lg: 8,
        md: 12,
        sm: 12,
        xs: 12
      },
      bgPicUrl: ''
    };
  },
  created: function created() {
    var state = this.$store.state.admin.mobildConfig;
    this.value = state.pageTitle;
    this.name = state.pageName;
    this.isShow = state.pageShow ? true : false;
    this.bgColor = state.pageColor ? true : false;
    this.bgPic = state.pagePic ? true : false;
    this.colorPicker = state.pageColorPicker;
    this.tabVal = state.pageTabVal;
    this.bgPicUrl = state.pagePicUrl;
  },
  methods: {
    // 点击图文封面
    modalPicTap: function modalPicTap(title) {
      this.modalPic = true;
    },
    bindDelete: function bindDelete() {
      this.bgPicUrl = '';
    },
    getPic: function getPic(pc) {
      var _this = this;

      this.$nextTick(function () {
        _this.bgPicUrl = pc.att_dir;
        _this.modalPic = false;

        _this.$store.commit('admin/mobildConfig/UPPICURL', pc.att_dir);
      });
    },
    colorPickerTap: function colorPickerTap(colorPicker) {
      this.$store.commit('admin/mobildConfig/UPPICKER', colorPicker);
    },
    radioTap: function radioTap(val) {
      this.$store.commit('admin/mobildConfig/UPRADIO', val);
    },
    changVal: function changVal(val) {
      this.$store.commit('admin/mobildConfig/UPTITLE', val.target.value);
    },
    changName: function changName(val) {
      this.$store.commit('admin/mobildConfig/UPNAME', val.target.value);
    },
    changeState: function changeState(val) {
      this.$store.commit('admin/mobildConfig/UPSHOW', val);
    },
    bgColorTap: function bgColorTap(val) {
      this.$store.commit('admin/mobildConfig/UPCOLOR', val);
    },
    bgPicTap: function bgPicTap(val) {
      this.$store.commit('admin/mobildConfig/UPPIC', val);
    }
  }
};
