import cardsData from "@/components/cards/cards";
import { workGroupStatistics, workGroupStatisticsList } from "@/api/work";
import echartsNew from "@/components/echartsNew/index";
import timeOptions from "@/utils/timeOptions";
export default {
  data: function data() {
    return {
      cardLists: [],
      optionData: {},
      style: {
        height: "400px"
      },
      spinShow: false,
      tableColumn: [{
        title: "时间",
        key: "create_time",
        minWidth: 120,
        align: "left"
      }, {
        title: "新增人员",
        key: "today_sum",
        minWidth: 100,
        align: "left"
      }, {
        title: "退群人员",
        key: "chat_return_sum",
        minWidth: 100,
        align: "left"
      }, {
        title: "当前群人员",
        key: "chat_sum",
        minWidth: 100,
        align: "left"
      }, {
        title: "当前退群人员",
        key: "today_return_sum",
        minWidth: 100,
        align: "center"
      }],
      tableData: [],
      userLoading: false,
      options: timeOptions,
      timeVal: [],
      tableForm: {
        page: 1,
        time: "",
        limit: 15,
        id: 0
      }
    };
  },
  components: {
    cardsData: cardsData,
    echartsNew: echartsNew
  },
  mounted: function mounted() {
    this.tableForm.id = this.$route.params.id;
    this.getData();
    this.getList();
  },
  methods: {
    // 选择时间
    selectChange: function selectChange(tab) {
      this.tableForm.page = 1;
      this.tableForm.time = tab;
      this.timeVal = [];
      this.getData();
      this.getList();
    },
    // 具体日期
    onchangeTime: function onchangeTime(e) {
      this.timeVal = e;
      this.tableForm.time = this.timeVal[0] ? this.timeVal.join("-") : "";
      this.tableForm.page = 1;
      this.getData();
      this.getList();
    },
    getList: function getList() {
      var _this = this;

      this.userLoading = true;
      workGroupStatisticsList(this.tableForm).then(function (res) {
        _this.tableData = res.data;
        _this.userLoading = false;
      }).catch(function (err) {
        _this.userLoading = false;

        _this.$Message.error(err.msg);
      });
    },
    getData: function getData() {
      var _this2 = this;

      workGroupStatistics({
        id: this.$route.params.id,
        time: this.tableForm.time
      }).then(function (res) {
        _this2.cardLists = [{
          col: 6,
          count: res.data.toDaySum,
          name: "今日新增人数",
          type: 1,
          className: "iconjinrixinzeng"
        }, {
          col: 6,
          count: res.data.toDayReturn,
          name: "今日退群人数",
          type: 1,
          className: "iconjinrituiqun"
        }, {
          col: 6,
          count: res.data.groupChatSum,
          name: "当前群人数",
          type: 1,
          className: "icondangqianqunchengyuan"
        }, {
          col: 6,
          count: res.data.groupChatReturnSum,
          name: "当前退群人数",
          type: 1,
          className: "iconleijituiqun"
        }];
        var xAxis = new Array();
        var series = [{
          name: "入群",
          data: [],
          type: "line",
          smooth: "true",
          yAxisIndex: 0
        }, {
          name: "退群",
          data: [],
          type: "line",
          smooth: "true",
          yAxisIndex: 0
        }];

        if (res.data.groupChatReturnList.length > res.data.groupChatList.length) {
          xAxis = res.data.groupChatReturnList.map(function (item) {
            return item.time;
          });
        } else {
          xAxis = res.data.groupChatList.map(function (item) {
            return item.time;
          });
        }

        series[0].data = res.data.groupChatList.map(function (item) {
          return item.sum;
        });
        series[1].data = res.data.groupChatReturnList.map(function (item) {
          return item.sum;
        });
        _this2.spinShow = true;
        var legend = series.map(function (item) {
          return item.name;
        });
        var col = ["#B37FEB", "#FFAB2B", "#1890FF", "#00C050"];
        series.map(function (item, index) {
          item.itemStyle = {
            normal: {
              color: col[index]
            }
          };
        });
        _this2.optionData = {
          tooltip: {
            trigger: "axis",
            axisPointer: {
              type: "cross",
              label: {
                backgroundColor: "#6a7985"
              }
            }
          },
          legend: {
            x: "center",
            data: legend
          },
          grid: {
            left: "3%",
            right: "4%",
            bottom: "3%",
            containLabel: true
          },
          toolbox: {
            feature: {
              saveAsImage: {}
            }
          },
          xAxis: {
            type: "category",
            boundaryGap: true,
            axisLabel: {
              interval: 0,
              rotate: 40,
              textStyle: {
                color: "#000000"
              }
            },
            data: xAxis
          },
          yAxis: [{
            type: "value",
            name: "数量",
            axisLine: {
              show: false
            },
            axisTick: {
              show: false
            },
            axisLabel: {
              textStyle: {
                color: "#7F8B9C"
              }
            },
            splitLine: {
              show: true,
              lineStyle: {
                color: "#F5F7F9"
              }
            }
          }],
          series: series
        };
        _this2.spinShow = false;
      });
    },
    pageChange: function pageChange(index) {
      this.tableForm.page = index;
      this.getList();
    }
  }
};
