import { getMember, memberSave, newcomerList } from "@/api/diy";
import uploadPic from './components/uploadPic';
export default {
  name: 'users',
  components: {
    uploadPic: uploadPic
  },
  props: {},
  data: function data() {
    return {
      swiperOption: {
        //显示分页
        pagination: {
          el: '.swiper-pagination'
        },
        //自动轮播
        autoplay: {
          delay: 2000,
          //当用户滑动图片后继续自动轮播
          disableOnInteraction: false
        },
        //开启循环模式
        loop: false
      },
      userData: {
        my_banner_status: true,
        routine_my_banner: [],
        routine_my_menus: [],
        status: '',
        order_status: '',
        menu_status: '',
        service_status: '',
        vip_type: 1,
        newcomer_style: 1,
        newcomer_status: 1
      },
      MyMenus: [{
        pic: '',
        url: '',
        name: '',
        sort: 1,
        status: 1,
        type: '1'
      }],
      storeMenu: [{
        pic: '',
        url: '',
        name: '',
        sort: 1,
        status: 1,
        type: '2'
      }],
      current: 1,
      colorStyle: '',
      order: {},
      newList: [],
      order01: {
        dfk: 'icondaifukuan1',
        dfh: 'icondaifahuo1',
        dsh: 'icondaishouhuo1',
        dpj: 'icondaipingjia1',
        sh: 'iconshouhou_tuikuan'
      },
      order02: {
        dfk: 'icondaifukuan-lan',
        dfh: 'icondaifahuo-lan',
        dsh: 'icondaishouhuo-lan',
        dpj: 'icondaipingjia-lan',
        sh: 'iconshouhou-tuikuan-lan'
      },
      order03: {
        dfk: 'icondaifukuan-ju',
        dfh: 'icondaifahuo-ju',
        dsh: 'icondaishouhuo-ju',
        dpj: 'icondaipingjia-ju',
        sh: 'iconshouhou-tuikuan-ju'
      },
      order04: {
        dfk: 'icondaifukuan-fen',
        dfh: 'icondaifahuo-fen',
        dsh: 'icondaishouhuo-fen',
        dpj: 'icondaipingjia-fen',
        sh: 'icona-shouhoutuikuan-fen'
      },
      order05: {
        dfk: 'icondaifukuan-lv',
        dfh: 'icondaifahuo-lv',
        dsh: 'icondaishouhuo-lv',
        dpj: 'icondaipingjia-lv',
        sh: 'iconshouhou-tuikuan-lv'
      },
      picStyle: ''
    };
  },
  created: function created() {
    this.getInfo();
    this.getNewcomerList();
  },
  methods: {
    getNewcomerList: function getNewcomerList() {
      var _this = this;

      newcomerList({
        page: 1,
        limit: 6,
        priceOrder: '',
        salesOrder: ''
      }).then(function (res) {
        _this.newList = res.data;
      }).catch(function (err) {
        return _this.$message.error(err.msg);
      });
    },
    currentShow: function currentShow(type) {
      this.current = type;
    },
    switchOrder: function switchOrder(status) {
      switch (status) {
        case 1:
          this.order = this.order01;
          break;

        case 2:
          this.order = this.order02;
          break;

        case 3:
          this.order = this.order03;
          break;

        case 4:
          this.order = this.order04;
          break;

        case 5:
          this.order = this.order05;
          break;

        default:
          this.order = this.order01;
          break;
      }
    },
    orderStyle: function orderStyle(e) {
      this.switchOrder(e);
    },
    getInfo: function getInfo() {
      var _this2 = this;

      var green = '--view-theme: #42CA4D;--view-priceColor:#FF7600;--view-minorColor:rgba(108, 198, 94, 0.5);--view-minorColorT:rgba(66, 202, 77, 0.1);--view-bntColor:#FE960F;';
      var red = '--view-theme: #e93323;--view-priceColor:#e93323;--view-minorColor:rgba(233, 51, 35, 0.5);--view-minorColorT:rgba(233, 51, 35, 0.1);--view-bntColor:#FE960F;';
      var blue = '--view-theme: #1DB0FC;--view-priceColor:#FD502F;--view-minorColor:rgba(58, 139, 236, 0.5);--view-minorColorT:rgba(9, 139, 243, 0.1);--view-bntColor:#22CAFD;';
      var pink = '--view-theme: #FF448F;--view-priceColor:#FF448F;--view-minorColor:rgba(255, 68, 143, 0.5);--view-minorColorT:rgba(255, 68, 143, 0.1);--view-bntColor:#282828;';
      var orange = '--view-theme: #FE5C2D;--view-priceColor:#FE5C2D;--view-minorColor:rgba(254, 92, 45, 0.5);--view-minorColorT:rgba(254, 92, 45, 0.1);--view-bntColor:#FDB000;';
      var picList = [require('@/assets/images/blue-bg.png'), require('@/assets/images/green-bg.png'), require('@/assets/images/red-bg.png'), require('@/assets/images/pink-bg.png'), require('@/assets/images/orange-bg.png')];
      getMember().then(function (res) {
        _this2.userData.status = res.data.status;
        _this2.userData.order_status = res.data.order_status;
        _this2.userData.my_banner_status = res.data.my_banner_status;
        _this2.userData.menu_status = res.data.menu_status;
        _this2.userData.service_status = res.data.service_status;
        _this2.userData.vip_type = res.data.vip_type;
        _this2.userData.newcomer_style = res.data.newcomer_style;
        _this2.userData.newcomer_status = res.data.newcomer_status;
        var storeMenu = [];
        var myMenu = [];

        _this2.switchOrder(res.data.order_status);

        switch (res.data.color_change) {
          case 1:
            _this2.colorStyle = blue;
            _this2.picStyle = picList[0];
            break;

          case 2:
            _this2.colorStyle = green;
            _this2.picStyle = picList[1];
            break;

          case 3:
            _this2.colorStyle = red;
            _this2.picStyle = picList[2];
            break;

          case 4:
            _this2.colorStyle = pink;
            _this2.picStyle = picList[3];
            break;

          case 5:
            _this2.colorStyle = orange;
            _this2.picStyle = picList[4];
            break;

          default:
            _this2.colorStyle = red;
            _this2.picStyle = picList[2];
            break;
        }

        res.data.routine_my_banner.forEach(function (el, index, arr) {
          if (el.pic.length) {
            el.pic = el.pic[0];
          }
        });

        if (res.data.routine_my_banner.length) {
          _this2.userData.routine_my_banner = res.data.routine_my_banner;
        } else {
          _this2.userData.routine_my_banner = [];
        }

        res.data.routine_my_menus.forEach(function (el, index, arr) {
          if (el.pic.length) {
            el.pic = el.pic[0];
          }

          if (el.type == "2") {
            storeMenu.push(el);
          } else {
            myMenu.push(el);
          }
        });

        if (myMenu.length) {
          _this2.MyMenus = myMenu;
        }

        if (storeMenu.length) {
          _this2.storeMenu = storeMenu;
        }
      });
    },
    onSubmit: function onSubmit() {
      var _this3 = this;

      this.userData.routine_my_menus = this.MyMenus.concat(this.storeMenu);
      this.$emit('parentFun', true);
      memberSave(this.userData).then(function (res) {
        _this3.$emit('parentFun', false);

        _this3.$Message.success(res.msg);
      }).catch(function (err) {
        _this3.$Message.error(err.msg);

        _this3.$emit('parentFun', false);
      });
    }
  }
};
