import util from "@/libs/util";
import Setting from "@/setting";
import addStore from "../components/addStore";
import { storeListApi, storeLogin, storeSetShowApi, resetApi } from "@/api/store";
export default {
  name: "storeList",
  components: {
    addStore: addStore
  },
  data: function data() {
    return {
      BaseURL: Setting.apiBaseURL.replace(/adminapi/, "store/home/"),
      total: 0,
      loading: false,
      formValidate: {
        type: "all",
        page: 1,
        limit: 15
      },
      columns: [{
        title: "ID",
        key: "id",
        width: 60
      }, {
        title: "门店图片",
        slot: "image",
        minWidth: 80
      }, {
        title: "门店名称",
        key: "name",
        minWidth: 80
      }, {
        title: "联系电话",
        key: "phone",
        minWidth: 90
      }, {
        title: "门店地址",
        key: "address",
        ellipsis: true,
        minWidth: 150
      }, {
        title: "营业时间",
        key: "day_time",
        minWidth: 120
      }, {
        title: "营业状态",
        key: "status_name",
        minWidth: 80
      }, {
        title: "操作",
        slot: "action",
        fixed: "right",
        minWidth: 240,
        align: "center"
      }],
      orderList: [{
        id: "1",
        order_id: "12",
        pay_price: "12",
        status: 1,
        phone: "13000000000",
        address: "陕西省西安市莲湖区大兴西路啊餐厨"
      }]
    };
  },
  mounted: function mounted() {
    this.getList();
  },
  methods: {
    getList: function getList() {
      var _this = this;

      this.loading = true;
      storeListApi(this.formValidate).then(function (res) {
        _this.orderList = res.data.list;
        _this.total = res.data.count;
        _this.loading = false;
      });
    },
    reset: function reset(row) {
      var _this2 = this;

      this.$modalForm(resetApi(row.id)).then(function () {
        return _this2.getList();
      });
    },
    add: function add() {
      this.$refs.template.title = "添加门店";
      this.$refs.template.add = 1;
      this.$refs.template.isTemplate = true;
      this.$refs.template.cityInfo({
        pid: 0
      });
    },
    edit: function edit(row) {
      this.$refs.template.title = "编辑门店";
      this.$refs.template.isTemplate = true;
      this.$refs.template.getInfo(row.id);
    },
    getExpiresTime: function getExpiresTime(expiresTime) {
      var nowTimeNum = Math.round(new Date() / 1000);
      var expiresTimeNum = expiresTime - nowTimeNum;
      return parseFloat(parseFloat(parseFloat(expiresTimeNum / 60) / 60) / 24);
    },
    // 进入门店
    gostore: function gostore(item) {
      var _this3 = this;

      storeLogin(item.id).then(function (res) {
        var data = res.data;
        var expires = data.expires_time;
        util.cookies.setStore("token", data.token, {
          expires: expires
        });
        util.cookies.setStore("uuid", data.user_info.id, {
          expires: expires
        });
        util.cookies.setStore("expires_time", expires, {
          expires: expires
        });
        var storage = window.localStorage;
        storage.setItem("menuListStore", JSON.stringify(data.menus));
        storage.setItem("uniqueAuthStore", JSON.stringify(data.unique_auth));
        var userInfoStore = {
          account: data.user_info.account,
          head_pic: data.user_info.avatar,
          logo: data.logo,
          logoSmall: data.logo_square,
          version: data.version
        };
        storage.setItem("userInfoStore", JSON.stringify(userInfoStore)); // menuListStore

        window.open(_this3.BaseURL);
      }).catch(function (err) {
        _this3.$Message.error(err.msg);
      });
    },
    delte: function delte(row, tit, num) {
      var _this4 = this;

      var delfromData = {
        title: tit,
        num: num,
        url: "store/store/del/".concat(row.id),
        method: "DELETE",
        ids: ""
      };
      this.$modalSure(delfromData).then(function (res) {
        _this4.$Message.success(res.msg);

        _this4.getList();
      }).catch(function (res) {
        _this4.$Message.error(res.msg);
      });
    },
    operation: function operation(row) {
      var _this5 = this;

      var a = 0; //修改营业状态的反值

      if (row.is_show == 0) {
        a = 1;
      }

      if (row.is_show == 1) {
        a = 0;
      }

      storeSetShowApi(row.id, a).then(function (res) {
        _this5.getList();

        _this5.$Message.success(res.msg);
      }).catch(function (err) {
        _this5.$Message.error(res.msg);
      });
    },
    searchs: function searchs() {},
    onClickTab: function onClickTab(e) {
      this.formValidate.page = 1;
      this.formValidate.type = e;
      this.getList();
    },
    //分页
    pageChange: function pageChange(status) {
      this.formValidate.page = status;
      this.getList();
    }
  }
};
