import vuedraggable from 'vuedraggable';
import uploadPictures from '@/components/uploadPictures';
export default {
  name: 'c_tab_bar',
  props: {
    name: {
      type: String
    },
    configData: {
      type: null
    }
  },
  components: {
    uploadPictures: uploadPictures,
    draggable: vuedraggable
  },
  data: function data() {
    return {
      modalPic: false,
      isChoice: '单选',
      gridBtn: {
        xl: 4,
        lg: 8,
        md: 8,
        sm: 8,
        xs: 8
      },
      gridPic: {
        xl: 6,
        lg: 8,
        md: 12,
        sm: 12,
        xs: 12
      },
      activeIndex: 0,
      isSelect: 0,
      datas: this.configData,
      lastObj: {}
    };
  },
  mounted: function mounted() {},
  watch: {
    configData: {
      handler: function handler(nVal, oVal) {
        this.datas = nVal;
      },
      deep: true
    }
  },
  methods: {
    // 添加模块
    addMenu: function addMenu() {
      if (this.configData[this.name].list.length == 0) {
        this.configData[this.name].list.push(this.lastObj);
      } else {
        var obj = JSON.parse(JSON.stringify(this.configData[this.name].list[this.configData[this.name].list.length - 1]));
        this.configData[this.name].list.push(obj);
      }
    },
    deleteMenu: function deleteMenu(index) {
      var _this = this;

      this.$Modal.confirm({
        title: '提示',
        content: '是否确定删除该菜单',
        onOk: function onOk() {
          if (_this.configData[_this.name].list.length == 1) {
            _this.lastObj = _this.configData[_this.name].list[0];
          }

          _this.configData[_this.name].list.splice(index, 1);
        },
        onCancel: function onCancel() {}
      });
    },
    // 点击图文封面
    modalPicTap: function modalPicTap(title, index, select) {
      this.activeIndex = index;
      this.modalPic = true;
      this.isSelect = select;
    },
    // 获取图片信息
    getPic: function getPic(pc) {
      var _this2 = this;

      this.$nextTick(function () {
        _this2.configData[_this2.name].list[_this2.activeIndex].imgList[_this2.isSelect] = pc.att_dir;
        _this2.modalPic = false;
      });
    }
  }
};
