import * as monaco from "monaco-editor";
export default {
  data: function data() {
    return {
      monacoInstance: null
    };
  },
  props: {
    value: {
      type: String,
      default: ""
    }
  },
  mounted: function mounted() {
    this.seteditor();
  },
  methods: {
    setValue: function setValue(val) {// this.monacoInstance.setValue(val)
    },
    seteditor: function seteditor() {
      var _this = this;

      // 初始化编辑器实例
      this.monacoInstance = monaco.editor.create(this.$refs.code_box, {
        value: this.value,
        theme: "vs",
        // vs, hc-black, or vs-dark
        language: "html",
        // shell、sql、python
        readOnly: false // 不能编辑

      }); // 编辑器内容发生改变时触发

      this.monacoInstance.onDidChangeModelContent(function () {
        _this.$emit('change', _this.monacoInstance.getValue());
      });
    }
  },
  beforeDestroy: function beforeDestroy() {
    this.monacoInstance.dispose();
    this.monacoInstance = null;
  }
};
