import ClipboardJS from "clipboard";
import uploadPictures from "@/components/uploadPictures";
import { addConfigApi, addStorageApi, storageListApi, storageSynchApi, storageStatusApi, editStorageApi, getConfigImageApi, saveBasicsApi } from "@/api/setting";
export default {
  name: 'setting_storage',
  components: {
    uploadPictures: uploadPictures
  },
  data: function data() {
    var isInteger = function isInteger(rule, value, callback) {
      if (!value || /(^(-)?[0-9]*$)/.test(value)) {
        callback();
      } else {
        callback(new Error('必须为整数'));
      }
    };

    return {
      modalPic: false,
      isChoice: "单选",
      gridBtn: {
        xl: 4,
        lg: 8,
        md: 8,
        sm: 8,
        xs: 8
      },
      gridPic: {
        xl: 6,
        lg: 8,
        md: 12,
        sm: 12,
        xs: 12
      },
      configuModal: false,
      configData: "",
      headerList: [{
        label: "基础配置",
        value: "1"
      }, {
        label: "阿里云储存",
        value: "3"
      }, {
        label: "腾讯云储存",
        value: "4"
      }, {
        label: "七牛云储存",
        value: "2"
      }],
      columns: [{
        title: "储存空间名称",
        key: "name",
        align: "center",
        minWidth: 200
      }, {
        title: "区域",
        key: "_region",
        align: "center",
        minWidth: 100
      }, {
        title: "空间域名",
        key: "domain",
        align: "center",
        minWidth: 200
      }, {
        title: "使用状态",
        slot: "status",
        align: "center",
        width: 90
      }, {
        title: "创建时间",
        key: "_add_time",
        align: "center",
        minWidth: 150
      }, {
        title: "更新时间",
        key: "_update_time",
        align: "center",
        minWidth: 150
      }, {
        title: "操作",
        slot: "action",
        width: 210,
        align: "center"
      }],
      total: 0,
      list: {
        page: 1,
        limit: 15,
        type: "1"
      },
      levelLists: [],
      currentTab: "1",
      loading: false,
      addData: {
        input: "",
        select: "",
        jurisdiction: "1",
        type: "1"
      },
      confData: {
        AccessKeyId: "",
        AccessKeySecret: ""
      },
      formData: {
        upload_type: 1,
        image_watermark_status: 0,
        thumb_big_width: '',
        thumb_big_height: '',
        thumb_mid_width: '',
        thumb_mid_height: '',
        thumb_small_width: '',
        thumb_small_height: '',
        watermark_type: 1,
        watermark_text: '',
        watermark_text_angle: '',
        watermark_text_color: '',
        watermark_text_size: '',
        watermark_position: 1,
        watermark_image: '',
        watermark_opacity: '',
        watermark_rotate: '',
        watermark_x: '',
        watermark_y: ''
      },
      rules: {
        thumb_big_width: [{
          validator: isInteger,
          trigger: 'blur'
        }],
        thumb_big_height: [{
          validator: isInteger,
          trigger: 'blur'
        }],
        thumb_mid_width: [{
          validator: isInteger,
          trigger: 'blur'
        }],
        thumb_mid_height: [{
          validator: isInteger,
          trigger: 'blur'
        }],
        thumb_small_width: [{
          validator: isInteger,
          trigger: 'blur'
        }],
        thumb_small_height: [{
          validator: isInteger,
          trigger: 'blur'
        }],
        watermark_x: [{
          validator: isInteger,
          trigger: 'blur'
        }],
        watermark_y: [{
          validator: isInteger,
          trigger: 'blur'
        }]
      },
      visible: false
    };
  },
  computed: {
    positionText: function positionText() {
      return ['左上', '中上', '右上', '左中', '中', '右中', '左下', '中下', '右下'][this.formData.watermark_position - 1];
    }
  },
  created: function created() {
    this.getConfigImage();
  },
  mounted: function mounted() {
    this.$nextTick(function () {
      var _this = this;

      var clipboard = new ClipboardJS(".copy-data");
      clipboard.on("success", function () {
        _this.$Message.success("复制成功");
      });
    });
  },
  methods: {
    //   获取缩略图配置
    getConfigImage: function getConfigImage() {
      var _this2 = this;

      getConfigImageApi().then(function (res) {
        var data = res.data;
        _this2.formData.upload_type = data.upload_type.value;
        _this2.formData.image_watermark_status = data.image_watermark_status.value;
        _this2.formData.thumb_big_width = data.thumb_big_width.value;
        _this2.formData.thumb_big_height = data.thumb_big_height.value;
        _this2.formData.thumb_mid_width = data.thumb_mid_width.value;
        _this2.formData.thumb_mid_height = data.thumb_mid_height.value;
        _this2.formData.thumb_small_width = data.thumb_small_width.value;
        _this2.formData.thumb_small_height = data.thumb_small_height.value;
        _this2.formData.watermark_type = data.watermark_type.value;
        _this2.formData.watermark_text = data.watermark_text.value;
        _this2.formData.watermark_text_angle = data.watermark_text_angle.value;
        _this2.formData.watermark_text_color = data.watermark_text_color.value;
        _this2.formData.watermark_text_size = data.watermark_text_size.value;
        _this2.formData.watermark_position = data.watermark_position.value;
        _this2.formData.watermark_image = data.watermark_image.value;
        _this2.formData.watermark_opacity = data.watermark_opacity.value;
        _this2.formData.watermark_rotate = data.watermark_rotate.value;
        _this2.formData.watermark_x = data.watermark_x.value;
        _this2.formData.watermark_y = data.watermark_y.value;
      });
    },
    //   基础配置保存
    handleSubmit: function handleSubmit(name) {
      var _this3 = this;

      this.$refs[name].validate(function (valid) {
        if (valid) {
          saveBasicsApi(_this3.formData).then(function (res) {
            _this3.$Message.success(res.msg);
          }).catch(function (res) {
            _this3.$Message.error(res.msg);
          });
        }
      });
    },
    // 选择图片
    modalPicTap: function modalPicTap() {
      this.modalPic = true;
    },
    // 选中图片
    getPic: function getPic(pc) {
      this.formData.watermark_image = pc.att_dir;
      this.modalPic = false;
    },
    // CNAME配置
    config: function config(row) {
      this.configuModal = true;
      this.configData = row;
    },
    //同步储存空间
    synchro: function synchro() {
      var _this4 = this;

      storageSynchApi(this.currentTab).then(function (res) {
        _this4.$Message.success(res.msg);

        _this4.getlist();
      }).catch(function (err) {
        _this4.$Message.error(err.msg);
      });
    },
    // 添加存储空间
    addStorageBtn: function addStorageBtn() {
      var _this5 = this;

      this.$modalForm(addStorageApi(this.currentTab)).then(function () {
        _this5.getlist();
      });
    },
    // 修改配置信息
    addConfigBtn: function addConfigBtn() {
      var _this6 = this;

      this.$modalForm(addConfigApi(this.currentTab)).then(function () {
        _this6.getlist();
      });
    },
    //修改空间域名
    edit: function edit(row) {
      var _this7 = this;

      this.$modalForm(editStorageApi(row.id)).then(function () {
        _this7.getlist();
      });
    },
    // 使用状态
    changeSwitch: function changeSwitch(row, item) {
      var _this8 = this;

      return new Promise(function (resolve) {
        _this8.$Modal.confirm({
          title: "切换状态",
          content: "您确认要切换使用状态吗？",
          onOk: function onOk() {
            // resolve();
            storageStatusApi(row.id).then(function (res) {
              _this8.$Message.success(res.msg);

              _this8.getlist();
            }).catch(function (err) {
              _this8.$Message.error(err.msg);
            });
          },
          onCancel: function onCancel() {
            _this8.$Message.info("已取消");

            _this8.getlist();
          }
        });
      });
    },
    // 云存储列表
    getlist: function getlist() {
      var _this9 = this;

      this.loading = true;
      storageListApi(this.list).then(function (res) {
        _this9.total = res.data.count;
        _this9.levelLists = res.data.list;
        _this9.loading = false;
      });
    },
    // 切换选项卡
    changeTab: function changeTab(data) {
      this.currentTab = data;
      this.list.type = data;
      this.list.page = 1;

      if (data == 1) {
        this.getConfigImage();
      } else {
        this.getlist();
      }
    },
    // 删除
    del: function del(row, tit, num) {
      var _this10 = this;

      var delfromData = {
        title: tit,
        num: num,
        url: "setting/config/storage/".concat(row.id),
        method: "DELETE",
        ids: ""
      };
      this.$modalSure(delfromData).then(function (res) {
        _this10.$Message.success(res.msg);

        _this10.getlist();
      }).catch(function (res) {
        _this10.$Message.error(res.msg);
      });
    },
    pageChange: function pageChange(index) {
      this.list.page = index;
      this.getlist();
    },
    handleView: function handleView() {
      this.visible = true;
    },
    handleRemove: function handleRemove() {
      this.formData.watermark_image = '';
    }
  }
};
