import uploadPictures from '@/components/uploadPictures';
import vuedraggable from 'vuedraggable';
export default {
  name: 'c_upload_list',
  props: {
    name: {
      type: String
    },
    configData: {
      type: null
    }
  },
  components: {
    uploadPictures: uploadPictures,
    draggable: vuedraggable
  },
  data: function data() {
    return {
      defaults: {},
      menus: [],
      modalPic: false,
      isChoice: '单选',
      gridBtn: {
        xl: 4,
        lg: 8,
        md: 8,
        sm: 8,
        xs: 8
      },
      gridPic: {
        xl: 6,
        lg: 8,
        md: 12,
        sm: 12,
        xs: 12
      },
      activeIndex: 0,
      lastObj: {},
      datas: this.configData
    };
  },
  mounted: function mounted() {},
  watch: {
    configData: {
      handler: function handler(nVal, oVal) {
        this.datas = nVal;
      },
      deep: true
    }
  },
  methods: {
    addBox: function addBox() {
      if (this.configData[this.name].list.length == 0) {
        this.configData[this.name].list.push(this.lastObj);
      } else {
        var obj = JSON.parse(JSON.stringify(this.configData[this.name].list[this.configData[this.name].list.length - 1]));
        this.configData[this.name].list.push(obj);
      }
    },
    // 点击图文封面
    modalPicTap: function modalPicTap(title, index) {
      this.activeIndex = index;
      this.modalPic = true;
    },
    // 添加自定义弹窗
    addCustomDialog: function addCustomDialog(editorId) {
      window.UE.registerUI('test-dialog', function (editor, uiName) {
        var dialog = new window.UE.ui.Dialog({
          iframeUrl: '/admin/widget.images/index.html?fodder=dialog',
          editor: editor,
          name: uiName,
          title: '上传图片',
          cssRules: 'width:1200px;height:500px;padding:20px;'
        });
        this.dialog = dialog; // 参考上面的自定义按钮

        var btn = new window.UE.ui.Button({
          name: 'dialog-button',
          title: '上传图片',
          cssRules: "background-image: url(../../../assets/images/icons.png);background-position: -726px -77px;",
          onclick: function onclick() {
            // 渲染dialog
            dialog.render();
            dialog.open();
          }
        });
        return btn;
      }, 37);
    },
    // 获取图片信息
    getPic: function getPic(pc) {
      var _this = this;

      this.$nextTick(function () {
        _this.configData[_this.name].list[_this.activeIndex].img = pc.att_dir;
        _this.modalPic = false;
      });
    },
    // 删除
    bindDelete: function bindDelete(item, index) {
      if (this.configData[this.name].list.length == 1) {
        this.lastObj = this.configData[this.name].list[0];
      }

      this.configData[this.name].list.splice(index, 1);
    }
  }
};
