/* 定义滑块 内阴影+圆角 */
::-webkit-scrollbar-thumb {
  -webkit-box-shadow: inset 0 0 6px #ddd;
}

::-webkit-scrollbar {
  width: 4px !important; /* 对垂直流动条有效 */
}

.tipInfo{
  font-size: 14px;
  margin-top: 3px;
  .info{
    font-size 12px
  }
}

.default {
  background-color: #fff;
  text-align: center;
  height: 50px;
  line-height: 50px;
  border-radius: 8px;
}

.listB {
  width: 100%;
  background-color: #fff;
  border-radius: 6px;

  .item {
    padding-left: 12px;
    color: #333;
    font-size: 12px;

    img {
      width: 17px;
      height: 17px;
      display: block;
    }

    .text {
      width: 227px;
      border-bottom: 1px solid #EEEEEE;
      padding: 10px 12px 10px 0;

      .iconfont {
        color: #8A8A8A;
        font-size: 12px;
      }
    }
  }
}

.dotted {
  border: 1px dashed #2d8cf0;
  cursor: pointer;
}

.solid {
  border: 1px solid #2d8cf0 !important;
}

.c_row-item {
  .slider-box {
    .info {
      font-size: 13px;
      color: #999999;
    }
  }
}

.swiper-pagination-fraction, .swiper-pagination-custom, .swiper-container-horizontal > .swiper-pagination-bullets {
  bottom: 2px;
}

/deep/.swiper-pagination-bullet {
  width: 4px;
  height: 4px;
}

/deep/.swiper-pagination-bullet-active {
  background: #fff;
}

.public {
  background: #FFFFFF;
  border-radius: 6px;
  margin-top: 6px;
  padding: 5px 8px 10px 8px;
  &.on{
    padding-top 8px;
  }
}

.users {
  .left {
    background: #F7F7F7;
    width: 310px;
    height: 550px;
    overflow-x: hidden;
    overflow-y: auto;
    padding-bottom 1px;
    border-radius: 10px;
    margin-right: 30px;
    border 1px solid #eee;

    .img {
      width: 100%;
      height: 310px;
      display block;
    }

    .details {
      width: 100%;
      position: relative;

      .iconfenxiang {
        position: absolute;
        top: 10px;
        right: 10px;
        font-size: 16px;
      }

      .details-one {
        .lable {
          color: var(--view-theme);
          font-size: 14px;
          font-weight bold
        }

        .details-add {
          display: inline-block;
          margin-right 2px;
        }

        .price {
          font-size: 21px;
          color: var(--view-theme);
          font-weight: 600;
        }

        .num {
          font-size: 14px;
          font-weight: 600;
          color: #333333;
          margin-left: 1px;
        }

        .vip {
          width: 28px;
          height: 10px;
          margin-left: 3px;
        }
      }

      .details-two {
        margin-top 5px;
        .banrd {
          width: 26px;
          height: 16px;
          text-align: center;
          line-height: 16px;
          border: 1px solid var(--view-theme);
          font-size: 8px;
          font-weight: 400;
          color: var(--view-theme);
          margin-right: 2px;
          padding: 1px 2px;
          border-radius: 3px;
        }

        .title {
          font-size: 14px;
          font-weight: 600;
          color: #333333;
        }
      }

      .details-three {
        font-size: 12px;
        font-weight: 400;
        color: #999999;
        display: flex;
        margin-top: 8px;
      }

      .open {
        margin-top: 9px;

        .img {
          width: 100%;
          height: 30px;
        }
      }
    }

    .discount {
      width: 100%;

      .name {
        font-size: 12px;
        font-weight: 400;
        color: #666666;
        &.on{
          margin-right 8px;
        }
      }

      .iconjinru {
        font-size: 9px;
        margin-left: 61px;
      }

      .list {
        .box {
          display: inline-block;
          width: 79px;
          height: 17px;
          border: 1px solid var(--view-theme);
          color: var(--view-theme);
          font-size: 12px;
          font-weight: 400;
          text-align: center;
          line-height: 17px;
          position: relative;
          margin-left: 7px;

          .yuan {
            width: 8px;
            height: 8px;
            border-radius: 50%;
            background-color: #fff;
            position: absolute;
            top: 4px;
            left: -6px;
            border-right: 1px solid var(--view-theme);
          }

          .yuan2 {
            width: 8px;
            height: 8px;
            border-radius: 50%;
            background-color: #fff;
            position: absolute;
            top: 4px;
            right: -6px;
            border-left: 1px solid var(--view-theme);
          }
        }
      }

      .activity {
        margin-top: 13px;
        &.on{
          margin-top 0;
        }

        .list {
          display: inline-block;
          width: 78px;
          height: 22px;
          background: var(--view-theme);
          border-radius: 16px;
          font-size: 12px;
          font-weight: 400;
          color: #FFFFFF;
          text-align: right;
          line-height: 22px;
          position: relative;
          padding-right: 9px;
          margin-left: 5px;
          .iconfont {
            font-size: 14px;
          }

          .iconmiaosha {
            font-size: 15px;
            position: absolute;
            top: 0px;
            left: 6px;
          }

          .iconkanjia {
            font-size: 15px;
            position: absolute;
            top: 0px;
            left: 6px;
          }
        }
      }

      .count {
        margin-top: 13px;
        &.on{
          margin-top 0;
        }

        .minus {
          display: inline-block;
          margin-left: 10px;
          width: 37px;
          height: 17px;
          text-align: center;
          line-height: 19px;
          background: var(--view-minorColorT);
          border-radius: 9px;
          color: var(--view-theme);
          font-size: 12px;
          font-weight: 400;
        }

        .text {
          font-size: 12px;
          font-weight: 400;
          color: #666666;
          margin-left: 4px;
        }

        .iconjinru {
          font-size: 9px;
          margin-left: 25px;
        }
      }
    }

    .specifications {
      width: 100%;
      &.on{
        padding-top 7px
      }

      .name {
        font-size: 12px;
        font-weight: 400;
        color: #666666;
      }

      .choice {
        position: relative;

        .text {
          font-size: 12px;
          font-weight: 400;
          color: #333333;
          margin-left: 7px;
        }

        .iconjinru {
          font-size: 9px;
          position: absolute;
          top: 2px;
          right: 0;
        }
      }

      .picture {
        display: flex;
        margin-top: 6px;
        padding-left: 45px;

        .img {
          width: 30px;
          height: 30px;
          margin-right: 7px;
          background-color: #fff;
        }

        .choose-color {
          width: 115px;
          height: 30px;
          background: #EEEEEE;
          border-radius: 3px;
          font-size: 12px;
          text-align: center;
          font-weight: 400;
          color: #999999;
          line-height: 33px;
        }
      }

      .parameter {
        margin-top: 13px;
        position: relative;
        &.on{
          margin-top 0;
        }

        .text {
          margin-left: 15px;
          font-size: 12px;
          font-weight: 400;
          color: #333333;
        }

        .iconjinru {
          font-size: 9px;
          position: absolute;
          top: 2px;
          right: 0px;
        }
      }

      .guarantee {
        margin-top: 13px;
        position: relative;
        &.on{
          margin-top 0
        }

        .text {
          margin-left: 15px;
          font-size: 12px;
          font-weight: 400;
          color: #333333;
        }

        .iconjinru {
          font-size: 9px;
          position: absolute;
          top: 2px;
          right: 0px;
        }
      }

      .store {
        margin-top: 13px;
        &.on{
          margin-top 0;
        }

        .store-up {
          position: relative;

          .img {
            width: 13px;
            height: 12px;
            margin-left: 15px;
            margin-right: 6px;
            background-color: #fff;
          }

          .store-name {
            font-size: 12px;
            font-weight: 400;
            color: #333333;
          }

          .iconjinru {
            font-size: 9px;
            position: absolute;
            top: 2px;
            right: 0px;
          }
        }

        .nearby {
          margin-top: 5px;
          margin-left: 40px;
          font-size: 12px;
          font-weight: 400;
          color: #333;

          .nearby-name {
            color: var(--view-theme) !important;
          }
        }
      }
    }

    .evaluate {
      margin-top: 8px;
      width: 100%;
      height: 245px;
      border-radius: 7px;

      .img {
        width: 100%;
        height: 100%;
        background: #FFFFFF;
        border-radius: 7px;
        display block;
      }
    }

    .collocation {
      margin-top: 8px;
      width: 100%;
      height: 128px;
      border-radius: 7px;

      .img {
        width: 100%;
        height: 100%;
        background: #FFFFFF;
        border-radius: 7px;
        display block
      }
    }

    .recommend {
      margin-top: 8px;
      width: 100%;
      height: 332px;
      border-radius: 7px;

      .img {
        width: 100%;
        height: 100%;
        background: #FFFFFF;
        border-radius: 7px;
        display block
      }
    }

    .product {
      margin-top: 8px;
      width: 100%;
      height: 390px;
      border-radius: 7px;

      .img {
        width: 100%;
        height: 100%;
        border-radius: 7px;
        display block
      }
    }

    // 购物车
    .shopping-cart {
      margin-top: 15px;
      width: 100%;
      height: 50px;
      display: flex;
      justify-content: space-between;

      .kefu {
        display: flex;
        flex-direction: column;
        font-size: 9px;
        color: #666666;

        .iconguanzhugongzhonghao {
          font-size: 15px;
        }
      }

      .btn {
        display: flex;
        font-size: 14px;
        font-weight: 400;
        color: #FFFFFF;

        .join {
          width: 111px;
          height: 38px;
          background: #FE960F;
          line-height: 38px;
          text-align: center;
          border-radius: 20px 0 0 20px;
        }

        .join-add {
          background-color: var(--view-theme) !important;
          border-radius: 0 20px 20px 0 !important;
        }
      }
    }

    .Tcenter {
      margin-top: -25px;
    }

    .header {
      background-color: var(--view-theme);
      background-image: url('../../../assets/images/user01.png');
      background-size: 100%;
      background-repeat: no-repeat;
      width: 100%;
      height: 112px;
      position: relative;
      margin-bottom: 12px;

      .img {
        width: 375px;
        height: 375px;
        background: pink;
      }

      .top {
        padding: 19px 20px 0 20px;

        .picTxt {
          .pictrue {
            width: 35px;
            height: 35px;
            border-radius: 50%;
            margin-right: 10px;

            img {
              width: 100%;
              height: 100%;
              border-radius: 50%;
            }
          }

          .txt {
            .name {
              font-size: 12px;
              color: #fff;
              font-weight: 600;

              img {
                width: 40px;
                height: 15px;
                margin-left: 7px;
                vertical-align: middle;
              }
            }

            .phone {
              width: 86px;
              height: 21px;
              border-radius: 13px;
              background-color: rgba(16, 55, 72, 0.2);
              font-size: 11px;
              color: #fff;
              margin-top: 4px;

              .iconfont {
                font-size: 11px;
              }
            }
          }
        }

        .news {
          position: relative;
          margin-left: 10px;

          .iconfont {
            font-size: 22px;
            color: #fff;
          }

          .num {
            position: absolute;
            width: 14px;
            height: 14px;
            background: #FFFFFF;
            border-radius: 50%;
            font-size: 9px;
            color: var(--view-theme);
            text-align: center;
            line-height: 14px;
            top: 3px;
            right: -4px;
          }
        }

        .iconerweima-xingerenzhongxin {
          font-size: 20px;
          color: #fff;
        }
      }

      .bottom {
        background-image: url('../../../assets/images/member.png');
        width: 310px;
        height: 34px;
        background-size: 100%;
        background-repeat: no-repeat;
        position: absolute;
        bottom: 0;
        padding: 0 23px 0 50px;
        font-size: 13px;
        color: #905100;

        .renew {
          font-size: 12px;

          .iconjinru {
            font-size: 11px;
          }
        }
      }
    }

    .orderCenter {
      background: #FFFFFF;
      border-radius: 8px;
      margin: 0 18px 10px 18px;
      text-align: center;
      padding: 15px 0;

      &.on {
        position: relative;
        margin-top: 10px;
      }

      .title {
        padding: 0 10px;
        font-size: 13px;
        color: #282828;
        font-weight: 600px;
        margin-bottom: 2px;

        .all {
          font-size: 12px;
          color: #666666;

          .iconfont {
            font-size: 12px;
            margin-left: 2px;
          }
        }
      }

      .list {
        margin-top: 10px;

        .item {
          font-size: 12px;
          color: #454545;

          .iconfont {
            font-size: 20px;
            color: var(--view-theme);
          }
        }
      }

      &.service {
        padding: 15px 0 0 0;
        margin-top: 10px;

        .list {
          .item {
            width: 25%;
            margin-bottom: 10px;

            .pictrue {
              width: 23px;
              height: 23px;
              margin: 0 auto 8px auto;
              font-size: 12px;

              img {
                width: 100%;
                height: 100%;
              }
            }
          }
        }
      }
    }
  }

  .right {
    width: 540px;

    /deep/.ivu-radio-wrapper {
      font-size: 13px;
      margin-right: 20px;
    }

    .line {
      margin-top: 30px;
      width: 100%;
      height: 1px;
      background-color: #EEEEEE;
    }

    .title {
      font-size: 14px;
      color: rgba(0, 0, 0, 0.85);
      position: relative;
      font-weight: bold;

      &:before {
        content: '';
        position: absolute;
        width: 2px;
        height: 14px;
        background: #1890FF;
        top: 50%;
        margin-top: -7px;
        left: -8px;
      }
    }

    .c_row-item {
      margin-top: 24px;
    }

    .add {
      margin-top: 15px !important;
    }

    .text {
      font-size: 12px;
      font-weight: 400;
      color: #999999;
    }
  }
}
