import WangEditor from "@/components/wangEditor/index.vue";
import Setting from "@/setting";
import { diyGetInfo, diySave, getColorChange } from "@/api/diy";
import { mapState } from "vuex";
import editFrom from "@/components/from/from";
import {
  groupAllApi,
  groupDataListApi,
  groupSaveApi,
  groupDataAddApi,
  groupDataHeaderApi,
  groupDataEditApi,
  groupDataSetApi,
  getAgreement,
  setAgreement,
  openAdvSave,
  getOpenAdv,
} from "@/api/system";
import draggable from "vuedraggable";
import uploadPictures from "@/components/uploadPictures";
import linkaddress from "@/components/linkaddress";
export default {
  name: "list",
  components: { editFrom, draggable, uploadPictures, linkaddress, WangEditor },
  computed: {
    bgcolors() {
      return {
        "--color-theme": this.bgCol,
      };
    },
    labelWidth() {
      return this.isMobile ? undefined : 120;
    },
    labelPosition() {
      return this.isMobile ? "top" : "right";
    },
    ...mapState("admin/layout", ["menuCollapse","isMobile"]),
  },
  data() {
    return {
      formValidate: {
        content: "",
      },
      agreementList: [
        { name: "隐私协议", type: "privacy" },
        { name: "用户协议", type: "user" },
        { name: "注销协议", type: "cancel" },
      ],
      ruleValidate: {},
      myConfig: {
        autoHeightEnabled: false, // 编辑器不自动被内容撑高
        initialFrameHeight: 500, // 初始容器高度
        initialFrameWidth: "100%", // 初始容器宽度
        UEDITOR_HOME_URL: "/admin/UEditor/",
        serverUrl: "",
      },
      agreementType: 0, //判断的隐私协议
      bgimg: 0,
      columns1: [],
      bgCol: "",
      name: "routine_home_bast_banner",
      grid: {
        xl: 7,
        lg: 7,
        md: 12,
        sm: 24,
        xs: 24,
      },
      loading: false,
      sginList: [],
      swiperOption: {
        //显示分页
        pagination: {
          el: ".swiper-pagination",
        },
        //设置点击箭头
        navigation: {
          nextEl: ".swiper-button-next",
          prevEl: ".swiper-button-prev",
        },
        //自动轮播
        autoplay: {
          delay: 2000,
          //当用户滑动图片后继续自动轮播
          disableOnInteraction: false,
        },
        //开启循环模式
        loop: false,
      },
      url: "",
      BaseURL: Setting.apiBaseURL.replace(/adminapi/, ""),
      pageId: 0,
      theme3: "light",
      tabList: [],
      lastObj: {
        add_time: "",
        comment: "",
        gid: "",
        id: "",
        img: "",
        link: "",
        sort: "",
        status: 1,
      },
      isChoice: "单选",
      modalPic: false,
      gridPic: {
        xl: 6,
        lg: 8,
        md: 12,
        sm: 12,
        xs: 12,
      },
      gridBtn: {
        xl: 4,
        lg: 8,
        md: 8,
        sm: 8,
        xs: 8,
      },
      groupAll: [],
      activeIndex: 0,
      sortName: null,
      activeIndexs: 0,
      cmsList: [],
      loadingExist: false,
      formItem: {
        time: "",
        type: "pic",
        status: 1,
        value: [],
        video_link: "",
      },
      guide: 0,
    };
  },
  created() {
    this.color();
  },
  mounted() {
    this.getGroupAll();
    this.info();
    this.url =
      this.BaseURL + "pages/columnGoods/HotNewGoods/index?type=1&name=精品推荐";
  },
  methods: {
    getEditorContent(data) {
      this.formValidate.content = data;
    },
    linkUrl(e) {
      this.tabList.list[this.activeIndexs].link = e;
    },
    color() {
      getColorChange("color_change").then((res) => {
        switch (res.data.status) {
          case 1:
            this.bgCol = "#3875EA";
            this.bgimg = 1;
            break;
          case 2:
            this.bgCol = "#00C050";
            this.bgimg = 2;
            break;
          case 3:
            this.bgCol = "#E93323";
            this.bgimg = 3;
            break;
          case 4:
            this.bgCol = "#FF448F";
            this.bgimg = 4;
            break;
          case 5:
            this.bgCol = "#FE5C2D";
            this.bgimg = 5;
            break;
        }
      });
    },
    // 添加表单
    groupAdd() {
      this.$modalForm(
        groupDataAddApi(
          { gid: this.pageId, config_name: this.name },
          "setting/group_data/create"
        )
      ).then(() => {
        this.url = this.BaseURL + "pages/users/user_sgin/index";
        this.info();
      });
    },
    info() {
      groupDataListApi({ config_name: this.name }, "setting/group_data")
        .then(async (res) => {
          this.tabList = res.data;
          if (this.name == "admin_login_slide") {
            this.tabList.list.forEach((item, index, array) => {
              if (typeof item.slide != "string" && item.slide != "undefined") {
                item.slide = item.slide[0];
              }
            });
          } else if (this.name == "sign_day_num") {
            this.cmsList = res.data.list;
          } else if (this.name == "user_recharge_quota") {
            this.sginList = res.data;
          } else {
            this.tabList.list.forEach((item, index, array) => {
              if (typeof item.img != "string" && item.img != "undefined") {
                item.img = item.img[0];
              }
            });
          }
        })
        .catch((res) => {
          this.loading = false;
          this.$Message.error(res.msg);
        });
    },
    edits(row) {
      this.pageId = row.id;
      this.name = row.config_name || "";

      if (["privacy", "user", "cancel"].includes(row)) {
        this.agreementType = row;
        this.guide = 0;
        this.formValidate.content = "";
        this.getAgreement(row);
      } else if (row == 2) {
        this.agreementType = "";
        this.guide = 2;
        this.name = "";
        getOpenAdv().then((res) => {
          res.data.status = parseInt(res.data.status);
          this.formItem = res.data;
          this.tabList.list = res.data.value;
        });
      } else {
        this.info();
        this.agreementType = "";
        this.guide = 0;
        switch (row.config_name) {
          case "routine_home_bast_banner":
            this.url =
              this.BaseURL +
              "pages/columnGoods/HotNewGoods/index?type=1&name=精品推荐";
            break;
          case "sign_day_num":
            this.url = "";
            this.getListHeader();
            break;
          case "combination_banner":
            this.url = this.BaseURL + "pages/activity/goods_combination/index";
            break;
          case "routine_home_hot_banner":
            this.url =
              this.BaseURL +
              "pages/columnGoods/HotNewGoods/index?type=2&name=热门榜单";
            break;
          case "routine_home_new_banner":
            this.url =
              this.BaseURL +
              "pages/columnGoods/HotNewGoods/index?type=3&name=首发新品";
            break;
          case "routine_home_benefit_banner":
            this.url =
              this.BaseURL +
              "pages/columnGoods/HotNewGoods/index?type=4&name=促销单品";
            break;
          case "user_recharge_quota":
            this.url = "";
            this.getListHeader();
            break;
          case "admin_login_slide":
            this.url = "";
            break;
          case "integral_shop_banner":
            this.url = "";
            break;
        }
      }
    },
    addBox() {
      if (this.tabList.list.length == 0) {
        this.tabList.list.push(this.lastObj);
        this.lastObj = {
          add_time: "",
          comment: "",
          gid: "",
          id: "",
          img: "",
          link: "",
          sort: "",
          status: 1,
        };
      } else {
        if (this.tabList.list.length == 5) {
          this.$Message.warning("最多添加五张呦");
        } else {
          let obj = JSON.parse(JSON.stringify(this.lastObj));
          this.tabList.list.push(obj);
        }
      }
    },
    // 删除
    bindDelete(item, index) {
      this.tabList.list.splice(index, 1);
    },
    // 点击图文封面
    modalPicTap(title, index) {
      this.activeIndex = index;
      this.modalPic = true;
    },
    // 获取图片信息
    getPic(pc) {
      this.$nextTick(() => {
        if (this.name == "admin_login_slide") {
          this.tabList.list[this.activeIndex].slide = pc.att_dir;
        } else {
          this.tabList.list[this.activeIndex].img = pc.att_dir;
        }
        this.modalPic = false;
      });
    },
    save() {
      if (["privacy", "user", "cancel"].includes(this.agreementType)) {
        this.onsubmit("formValidate");
      } else if (this.guide == 2) {
        this.formItem.value = this.tabList.list;
        openAdvSave(this.formItem)
          .then((res) => {
            this.loadingExist = false;
            this.$Message.success(res.msg);
          })
          .catch((err) => {
            this.loadingExist = false;
            this.$Message.error(err.msg);
          });
      } else {
        this.loadingExist = true;
        groupSaveApi({
          gid: this.pageId,
          config_name: this.name,
          data: this.tabList.list,
        })
          .then((res) => {
            this.loadingExist = false;
            this.$Message.success(res.msg);
          })
          .catch((err) => {
            this.loadingExist = false;
            this.$Message.error(err.msg);
          });
      }
    },
    link(index) {
      this.activeIndexs = index;
      this.$refs.linkaddres.modals = true;
    },
    getListHeader() {
      this.loading = true;
      groupDataHeaderApi({ config_name: this.name }, "setting/sign_data/header")
        .then((res) => {
          let data = res.data;
          let header = data.header;
          let index = [];
          this.columns1 = header;
          this.loading = false;
        })
        .catch((res) => {
          this.loading = false;
          this.$Message.error(res.msg);
        });
    },
    // 编辑
    edit(row) {
      this.$modalForm(
        groupDataEditApi(
          { gid: this.pageId, config_name: this.name },
          "setting/group_data/" + row.id + "/edit"
        )
      ).then(() => {
        this.info();
        this.url = this.BaseURL + "pages/users/user_sgin/index";
      });
    },
    // 删除
    del(row, tit, num) {
      let delfromData = {
        title: tit,
        num: num,
        url: "setting/group_data/" + row.id,
        method: "DELETE",
        ids: "",
      };
      this.$modalSure(delfromData)
        .then((res) => {
          this.info();
          this.$Message.success(res.msg);
        })
        .catch((res) => {
          this.$Message.error(res.msg);
        });
    },
    // 修改是否显示
    onchangeIsShow(row) {
      groupDataSetApi(
        "setting/group_data/set_status/" + row.id + "/" + row.status
      )
        .then(async (res) => {
          this.url = this.BaseURL + "/pages/users/user_sgin/index";
          this.$Message.success(res.msg);
          this.info();
        })
        .catch((res) => {
          this.url = this.BaseURL + "/pages/users/user_sgin/index";
          this.$Message.error(res.msg);
        });
    },
    getGroupAll() {
      groupAllApi()
        .then(async (res) => {
          this.groupAll = res.data;
          this.sortName = res.data[0].config_name;
          this.pageId = res.data[0].id;
        })
        .catch((res) => {
          this.$Message.error(res.msg);
        });
    },
    getContent(val) {
      this.formValidate.content = val;
    },
    // 提交数据
    onsubmit(name) {
      this.$refs[name].validate((valid) => {
        if (valid) {
          setAgreement(this.formValidate, this.agreementType)
            .then(async (res) => {
              this.$Message.success(res.msg);
            })
            .catch((res) => {
              this.$Message.error(res.msg);
            });
        } else {
          return false;
        }
      });
    },
    //详情
    getAgreement(type) {
      getAgreement(type)
        .then(async (res) => {
          let data = res.data;
          this.formValidate = {
            content: data.content,
          };
        })
        .catch((res) => {
          this.loading = false;
          this.$Message.error(res.msg);
        });
    },
    addCustomDialog(editorId) {
      window.UE.registerUI(
        "test-dialog",
        function (editor, uiName) {
          // 创建 dialog
          let dialog = new window.UE.ui.Dialog({
            // 指定弹出层中页面的路径，这里只能支持页面，路径参考常见问题 2
            iframeUrl: "/admin/widget.images/index.html?fodder=dialog",
            // 需要指定当前的编辑器实例
            editor: editor,
            // 指定 dialog 的名字
            name: uiName,
            // dialog 的标题
            title: "上传图片",
            // 指定 dialog 的外围样式
            cssRules: "width:1200px;height:500px;padding:20px;",
          });
          this.dialog = dialog;
          var btn = new window.UE.ui.Button({
            name: "dialog-button",
            title: "上传图片",
            cssRules: `background-image: url(../../../assets/images/icons.png);background-position: -726px -77px;`,
            onclick: function () {
              // 渲染dialog
              dialog.render();
              dialog.open();
            },
          });
          return btn;
        },
        37
      );
    },
  },
};
