import { mapState } from 'vuex';
import { adminListApi, adminFromApi, adminEditFromApi, setShowApi } from '@/api/systemAdmin';
import adminFrom from '../../../components/from/from';
export default {
    name: 'systemAdmin',
    components: { adminFrom },
    data () {
        return {
            grid: {
                xl: 7,
                lg: 7,
                md: 12,
                sm: 24,
                xs: 24
            },
            total: 0,
            loading: false,
            roleData: {
                status1: ''
            },
            formValidate: {
                roles: '',
                status: '',
                name: '',
                page: 1, // 当前页
                limit: 20 // 每页显示条数
            },
            list: [],
            columns1: [
                {
                    title: '姓名',
                    key: 'real_name',
                    minWidth: 120
                },
                {
                    title: '账号',
                    key: 'account',
                    minWidth: 150
                },
                {
                    title: '身份',
                    slot: 'roles',
                    minWidth: 250
                },
                {
                    title: '最后一次登录时间',
                    key: '_last_time',
                    minWidth: 180
                },
                {
                    title: '最后一次登录ip',
                    key: 'last_ip',
                    minWidth: 180
                },
                {
                    title: '开启',
                    slot: 'status',
                    minWidth: 90
                },
                {
                    title: '操作',
                    key: 'action',
                    slot: 'action',
                    fixed: 'right',
                    minWidth: 120
                }
            ],
            FromData: null,
            modalTitleSs: '',
            ids: Number
        }
    },
    computed: {
        ...mapState('admin/layout', [
            'isMobile'
        ]),
        labelWidth () {
            return this.isMobile ? undefined : 96;
        },
        labelPosition () {
            return this.isMobile ? 'top' : 'right';
        }
    },
    created () {
        this.getList();
    },
    methods: {
        // 修改是否开启
        onchangeIsShow (row) {
            let data = {
                id: row.id,
                status: row.status
            }
            setShowApi(data).then(async res => {
                this.$Message.success(res.msg);
            }).catch(res => {
                this.$Message.error(res.msg);
            })
        },
        // 请求列表
        submitFail () {
            this.getList();
        },
        // 列表
        getList () {
            this.loading = true;
            this.formValidate.roles = this.formValidate.roles || ''
            adminListApi(this.formValidate).then(async res => {
                this.total = res.data.count;
                this.list = res.data.list;
                this.loading = false;
            }).catch(res => {
                this.loading = false;
                this.$Message.error(res.msg);
            })
        },
        pageChange (index) {
            this.formValidate.page = index
            this.getList();
        },
        // 添加表单
        add () {
            adminFromApi().then(async res => {
                this.FromData = res.data;
                this.$refs.adminfrom.modals = true;
            }).catch(res => {
                this.$Message.error(res.msg);
            })
        },
        // 编辑
        edit (row) {
            adminEditFromApi(row.id).then(async res => {
                if (res.data.status === false) {
                    return this.$authLapse(res.data);
                }
                this.FromData = res.data;
                this.$refs.adminfrom.modals = true;
            }).catch(res => {
                this.$Message.error(res.msg);
            })
        },
        // 删除
        del (row, tit, num) {
            let delfromData = {
                title: tit,
                num: num,
                url: `setting/admin/${row.id}`,
                method: 'DELETE',
                ids: ''
            };
            this.$modalSure(delfromData).then((res) => {
                this.$Message.success(res.msg);
                this.list.splice(num, 1);
            }).catch(res => {
                this.$Message.error(res.msg);
            });
        },
        // 表格搜索
        userSearchs () {
            this.formValidate.page = 1;
            this.list = [];
            this.getList();
        }
    }
}
