import {
  speeChcraft,
  serviceCate,
  serviceCateUpdate,
  addSpeeChcraft,
  addServiceCate,
  editServiceCate,
} from "@/api/kefu";

export default {
  name: "msgWindow",
  data() {
    return {
      ops: {
        vuescroll: {
          mode: "native",
          enable: false,
          tips: {
            deactive: "Push to Load",
            active: "Release to Load",
            start: "Loading...",
            beforeDeactive: "Load Successfully!",
          },
          auto: false,
          autoLoadDistance: 0,
          pullRefresh: {
            enable: false,
          },
          pushLoad: {
            enable: false,
            auto: true,
            autoLoadDistance: 10,
          },
        },
        bar: {
          background: "#393232",
          opacity: ".5",
          size: "2px",
        },
      },
      isScroll: true,
      page: 1,
      limit: 10,
      tabCur: 1,
      tabList: [
        {
          title: "个人库",
          key: 1,
        },
        {
          title: "公共库",
          key: 0,
        },
      ],
      searchTxt: "", // 搜索
      list: [
        {
          isEdit: false,
        },
      ], // 列表
      model1: "",
      msgTitle: "", // 填写的标题
      sortList: [], // 分类
      cateId: "", // 选中的id
      addMsg: {
        title: "",
        message: "",
        cateId: "",
        isEdit: false,
      },
      isAddSort: false, // 添加分类
      classTitle: "", // 分类名称
      classSort: "", // 分类排序
      maskTitle: "", // 弹窗标题
      editObj: {}, // 编辑分类对象
      isSearchType: false,
      oldTitle: "",
      oldMsg: "",
    };
  },
  filters: {
    filtersTitle(val) {
      let len = 37;
      if (val.length > len) {
        let data = val.substring(0, len);
        return `${data}...`;
      } else {
        return val;
      }
    },
    filtersCon(val) {
      let len = 113;
      if (val.length > len) {
        let data = val.substring(0, len);
        return `${data}...`;
      } else {
        return val;
      }
    },
  },
  mounted() {
    let self = this;
    this.serviceCate();
    this.$nextTick(() => {
      this.scroll = new BScroll(this.$refs.wrapper, {
        mouseWheel: {
          speed: 20,
          invert: false,
          easeTime: 300,
        },
        scrollbar: true,
        disableMouse: true,
        // and so on
      });
    });
  },
  methods: {
    // 关闭编辑
    editClose(item) {
      if (this.oldTitle && this.oldMsg) {
        item.title = this.oldTitle;
        item.message = this.oldMsg;
      }
      item.isEdit = false;
    },
    // 打开编辑
    editMsg(item) {
      this.oldTitle = JSON.parse(JSON.stringify(item.title));
      this.oldMsg = JSON.parse(JSON.stringify(item.message));
      item.isEdit = true;
      this.cateId = item.cate_id;
    },
    // 编辑框
    bindEdit(item, index) {
      if (index == 0) {
        return;
      } else {
        item.isEdit = !item.isEdit;
      }
    },
    // 头部选择
    bindTab(item) {
      this.tabCur = item.key;
      this.cateId = "";
      this.sortList = [];
      this.isScroll = true;
      this.page = 1;
      this.list = [];
      this.serviceCate();
    },
    // 搜索
    bindSearch() {
      if (this.isSearchType) return;
      this.isScroll = true;
      this.page = 1;
      this.list = [];
      this.getList();
      this.isSearchType = true;
    },
    // 选择分类
    selectSort(item) {
      if (this.cateId == item.id) {
        return;
      }
      this.sortList.forEach((el, index) => {
        if (el.id != item.id) {
          el.isEdit = false;
        }
      });
      this.cateId = item.id;
      this.isScroll = true;
      this.page = 1;
      this.list = [];
      this.getList();
    },
    // 删除分类
    delSort(row, tit, num) {
      let delfromData = {
        title: tit,
        num: num,
        url: `/service/cate/${row.id}`,
        method: "DELETE",
        ids: "",
        kefu: true,
      };
      this.$modalSure(delfromData)
        .then((res) => {
          this.$Message.success(res.msg);
          this.isScroll = true;
          this.page = 1;
          this.list = [];
          this.cateId = "";
          this.serviceCate();
        })
        .catch((res) => {
          this.$Message.error(res.msg);
        });
    },
    // 获取分类
    serviceCate() {
      serviceCate({
        type: this.tabCur,
      }).then((res) => {
        let obj = {
          id: "",
          name: "全部",
        };
        res.data.data.forEach((el, index) => {
          el.isEdit = false;
        });
        res.data.data.unshift(obj);
        this.sortList = res.data.data;
        if (this.cateId === "") {
          this.cateId = res.data.data[0].id;
        }
        this.getList();
      });
    },
    // 获取列表
    getList() {
      if (!this.isScroll) return;
      speeChcraft({
        page: this.page,
        limit: this.limit,
        title: this.searchTxt,
        cate_id: this.cateId,
        type: this.tabCur,
      })
        .then((res) => {
          this.isScroll = res.data.length >= this.limit;
          res.data.forEach((el, index) => {
            el.isEdit = false;
          });
          this.page++;
          this.list = this.list.concat(res.data);
          this.isSearchType = false;
        })
        .catch((error) => {
          this.isSearchType = false;
        });
    },
    // 修改话术
    updataMsg(item) {
      serviceCateUpdate(item.id, {
        title: item.title,
        cate_id: this.cateId,
        message: item.message,
      })
        .then((res) => {
          this.$Message.success("修改成功");
          item.isEdit = false;
        })
        .catch((error) => {
          this.$Message.error(error.msg);
          item.isEdit = true;
        });
    },
    // 添加框显示
    bindFocus() {
      this.list.forEach((el, item) => {
        el.isEdit = false;
      });
      this.addMsg.isEdit = true;
    },
    // 打开添加窗口
    openAddSort() {
      this.editObj.id = "";
      this.isAddSort = true;
      this.maskTitle = "添加分组";
    },
    // 添加话术
    bindAdd() {
      addSpeeChcraft({
        title: this.addMsg.title,
        cate_id: this.addMsg.cateId,
        message: this.addMsg.message,
      })
        .then((res) => {
          this.addMsg.title = "";
          this.addMsg.message = "";
          this.addMsg.cateId = "";
          this.addMsg.isEdit = false;
          this.$Message.success(res.msg);
          res.data.isEdit = false;
          this.page = 1;
          this.list = [];
          this.isScroll = true;
          this.serviceCate();
        })
        .catch((error) => {
          this.$Message.error(error.msg);
        });
    },
    // 删除
    delMsg(row, tit, num, type) {
      let delfromData = {
        title: tit,
        num: num,
        url: `service/speechcraft/${row.id}`,
        method: "DELETE",
        ids: "",
        kefu: true,
      };
      this.$modalSure(delfromData)
        .then((res) => {
          this.$Message.success(res.msg);
          this.list.splice(num, 1);
        })
        .catch((res) => {
          this.$Message.error(res.msg);
        });
    },
    // 添加分类
    addServiceCate() {
      if (this.editObj.id) {
        editServiceCate(this.editObj.id, {
          name: this.classTitle,
          sort: this.classSort,
        })
          .then((res) => {
            this.classTitle = "";
            this.classSort = "";
            this.$Message.success(res.msg);
            this.isAddSort = false;
            this.page = 1;
            this.list = [];
            this.isScroll = true;
            this.serviceCate();
          })
          .catch((error) => {
            this.classTitle = "";
            this.classSort = "";
            this.$Message.error(res.msg);
          });
      } else {
        addServiceCate({
          name: this.classTitle,
          sort: this.classSort,
        })
          .then((res) => {
            this.classTitle = "";
            this.classSort = "";
            this.$Message.success(res.msg);
            this.isAddSort = false;
            this.page = 1;
            this.list = [];
            this.isScroll = true;
            this.serviceCate();
          })
          .catch((error) => {
            this.classTitle = "";
            this.classSort = "";
            this.$Message.error(res.msg);
          });
      }
    },
    // 编辑分类
    editSort(item) {
      this.classSort = item.sort;
      this.classTitle = item.name;
      this.isAddSort = true;
      this.maskTitle = "编辑分组";
      this.editObj = item;
    },
    handleReachBottom() {
      this.getList();
    },
    bindRadio(data) {
      this.$emit("activeTxt", data.message);
    },
  },
};
