import { mapState } from "vuex";
import {workGroupSynch,workGroupChat,getGroupChatMember} from "@/api/work"
import timeOptions from "@/utils/timeOptions"
export default {
  name: "",
  data() {
    return {
      loading: false,
      userLoading:false,
      formInline:{},
      clentStatus:false,
      options: timeOptions,
      columns1: [
        {
          title: "ID",
          key: "id",
          minWidth: 40,
          maxWidth: 80,
        },
        {
          title: "群名称",
          key: "name",
          minWidth: 80,
        },
        {
          title: "群主",
          slot: "ownerInfo",
          minWidth: 100,
        },
        {
          title: "群公告",
          key: "notice",
          minWidth: 100,
        },
        {
          title: "管理员",
          slot: "admin_user_list",
          minWidth: 120,
        },
        {
          title: "创建时间",
          key: "group_create_time",
          minWidth: 110,
        },
        {
          title: "群人数",
          key: "member_num",
          minWidth: 80,
        },
        {
          title: "退群人数",
          key: "retreat_group_num",
          minWidth: 80,
        },
        {
          title: "操作",
          slot: "action",
          // fixed: "right",
          minWidth: 130,
        },
      ],
      tableData: [],
      clientColumn:[
        {
          title: "ID",
          key: "id",
          minWidth: 60,
          align: 'center'
        },
        {
          title: "姓名",
          slot: "member",
          minWidth: 80,
          align: 'center'
        },
        {
          title: "成员类型",
          slot: "type",
          minWidth: 80,
          align: 'center'
        },
        {
          title: "入群方式",
          slot: "join_scene",
          minWidth: 80,
          align: 'center'
        },
        {
          title: "群昵称",
          key: "group_nickname",
          minWidth: 60,
          align: 'center'
        },
        {
          title: "入群时间",
          slot: "join_time",
          minWidth: 100,
          align: 'center'
        },
      ],
      clientList:[],
      grid: {
        xl: 7,
        lg: 10,
        md: 12,
        sm: 24,
        xs: 24,
      },
      timeVal:[],
      tableFrom: {
        time: "",
        name: "",
        page: 1,
        limit: 15,
      },
      clientForm:{
        page: 1,
        limit: 15,
      },
      total: 0,
    };
  },
  filters:{
    scene(value){
      if(value == 1){
        return '直接邀请'
      }else if(value == 2){
        return '链接邀请'
      }else if(value == 3){
        return '扫码入群'
      }
    }
  },
  computed: {
    ...mapState("admin/layout", ["isMobile"]),
    labelWidth() {
      return this.isMobile ? undefined : 80;
    },
    labelPosition() {
      return this.isMobile ? "top" : "left";
    },
  },
  created() {
    this.getWorkGroupChat();
  },
  methods: {
    getWorkGroupChat(){
      this.loading = true;
      workGroupChat(this.tableFrom).then(res=>{
        this.tableData = res.data;
        this.loading = false;
      }).catch(err=>{
        this.$Message.error(err.msg)
        this.loading = false;
      })
    },
    groupSynch(){
      workGroupSynch().then(res=>{
        this.$Message.success(res.msg);
      })
    },
    statistical(row,index){
      this.$router.push('/admin/work/client/statistical/' + row.id)
    },
    groupMember(row,index){
      this.clentStatus = true;
      this.userLoading = true;
      this.$set(this.clientForm,'id',row.id);
      getGroupChatMember(this.clientForm).then(res=>{
        this.clientList = res.data;
        this.userLoading = false;
      }).catch(err=>{
        this.$Message.error(err.msg);
      })
    },
    onchangeTime(e){
      this.timeVal = e;
      this.tableFrom.time = this.timeVal.join("-");
      this.tableFrom.page = 1;
      this.getWorkGroupChat();
    },
    searchData(){
      this.tableFrom.page = 1;
      this.getWorkGroupChat();
    },
    pageChange(index){
      this.tableFrom.page = index;
      this.getWorkGroupChat();
    },
    clientChange(index){
      this.clientForm.page = index;
      this.userLoading = true;
      getGroupChatMember(this.clientForm).then(res=>{
        this.clientList = res.data;
        this.userLoading = false;
      }).catch(err=>{
        this.$Message.error(err.msg);
      })
    }
  },
};
