import uploadPictures from '@/components/uploadPictures'
import {
  auth,
  getVersion,
  crmebProduct,
  crmebCopyRight,
  saveCrmebCopyRight,
  getCrmebCopyRight,
} from '@/api/system'
import { mapState } from 'vuex'
import { formatDate } from '@/utils/validate'
import QRCode from 'qrcodejs2'
import Vcode from 'vue-puzzle-vcode'

export default {
  name: 'system_auth',
  computed: {
    ...mapState('admin/layout', ['isMobile']),
    ...mapState('admin/userLevel', ['categoryId']),
    labelWidth() {
      return this.isMobile ? undefined : 85
    },
    labelPosition() {
      return this.isMobile ? 'top' : 'right'
    },
  },

  data() {
    return {
      // baseUrl: 'https://shop.crmeb.net/html/index.html',
      baseUrl: 'https://www.higoshop.net/auth/index.html',
      iframeUrl: '',
      captchs: 'http://authorize.crmeb.net/api/captchs/',
      // captchs: 'https://mall.higoshop.net/api/captchs/',
      authCode: '',
      status: 1,
      dayNum: 0,
      copyright: '',
      isTemplate: false,
      price: '',
      proPrice: '',
      productStatus: false,
      copyrightText: '',
      success: false,
      payType: '',
      disabled: false,
      isShow: false, // 验证码模态框是否出现
      active: 0,
      timer: null,
      version: '',
      label: '',
      productType: '',
      modalPic: false,
      isChoice: '单选',
      authorizedPicture: '', // 版权图片
      gridPic: {
        xl: 6,
        lg: 8,
        md: 12,
        sm: 12,
        xs: 12,
      },
      gridBtn: {
        xl: 4,
        lg: 8,
        md: 8,
        sm: 8,
        xs: 8,
      },
    }
  },
  filters: {
    formatDate(time) {
      if (time !== 0) {
        let date = new Date(time * 1000)
        return formatDate(date, 'yyyy-MM-dd hh:mm')
      }
    },
  },
  components: {
    Vcode,
    uploadPictures,
  },
  mounted() {
    this.getAuth()
    this.getVersion()
    window.addEventListener('message', (e) => {
      if (e.data.event === 'onCancel') {
        this.cancel()
      }
    })
  },
  methods: {
    getVersion() {
      getVersion().then((res) => {
        this.version = res.data.version
        this.label = res.data.label
      })
    },
    getCrmebCopyRight() {
      crmebCopyRight().then((res) => {
        this.getAuth()
        return this.$Message.success(res.msg)
      })
    },
    //保存版权信息
    saveCopyRight() {
      saveCrmebCopyRight({
        copyright: this.copyrightText,
        copyright_img: this.authorizedPicture,
      }).then((res) => {
        return this.$Message.success(res.msg)
      })
    },
    // 选择图片
    modalPicTap() {
      this.modalPic = true
    },
    // 选中图片
    getPic(pc) {
      this.authorizedPicture = pc.att_dir
      this.modalPic = false
    },
    //获取版权信息
    getCopyRight() {
      getCrmebCopyRight().then((res) => {
        this.copyrightText = res.data.copyrightContext || ''
        this.authorizedPicture = res.data.copyrightImage || ''
      })
    },
    cancel() {
      if (this.productType === 'copyright') {
        this.getCrmebCopyRight()
      } else {
        this.getAuth()
      }
      this.iframeUrl = ''
      this.isTemplate = false
    },
    loginTabSwitch(index) {
      this.active = index
    },
    getAuth() {
      auth()
        .then((res) => {
          let data = res.data || {}
          this.authCode = data.authCode || ''
          this.status = data.status === undefined ? -1 : data.status
          this.dayNum = data.day || 0
          this.copyright = data.copyright
          if (this.copyright) {
            this.getCopyRight()
          }
        })
        .catch((err) => {
          this.$Message.error(err.msg)
        })
    },
    toCrmeb() {
      window.open('https://www.higoshop.net')
    },
    getProduct() {
      crmebProduct({ type: 'copyright' })
        .then((res) => {
          this.price = res.data.attr.price
          this.productStatus = true
        })
        .catch((err) => {
          this.$Message.error(err.msg)
        })
      crmebProduct({ type: 'pro' })
        .then((res) => {
          this.proPrice = res.data.attr.price
        })
        .catch((err) => {
          this.$Message.error(err.msg)
        })
    },
    payment(product) {
      this.productType = product
      let host = location.host
      let hostData = host.split('.')
      if (hostData[0] === 'test' && hostData.length === 4) {
        host = host.replace('test.', '')
      } else if (hostData[0] === 'www' && hostData.length === 3) {
        host = host.replace('www.', '')
      }
      this.iframeUrl =
        this.baseUrl +
        '?url=' +
        host +
        '&product=' +
        product +
        '&version=' +
        this.version +
        '&label=' +
        this.label
      this.isTemplate = true
    },
    // 用户点击遮罩层，应该关闭模态框
    onClose() {
      this.isShow = false
    },
  },
  destroyed() {},
}
