.order_box{
    box-sizing: border-box;
    .header{
        margin: 25px 24px;
        .pictrue{
            width: 60px;
            height: 60px;
            img{
                width: 100%;
                height: 100%;
                display: block;
            }
        }
        .text{
            margin-left: 12px;
            .name{
                font-size: 16px;
                font-weight: 500;
                color: rgba(0,0,0,0.85);
            }
            .info{
                font-size: 13px;
                color: #606266;
                margin-top: 3px;
            }
        }
    }
    .pictrueBox {
        display: inline-block;
        .upLoad {
            width: 58px;
            height: 58px;
            line-height: 58px;
            border: 1px dotted rgba(0, 0, 0, 0.1);
            border-radius: 4px;
            background: rgba(0, 0, 0, 0.02);
            cursor: pointer;
            .input-display {
                display: none
            }
        }
        .pictrue {
            width: 60px;
            height: 60px;
            border: 1px dotted rgba(0, 0, 0, 0.1);
            margin-right: 15px;
            margin-bottom: 10px;
            display: inline-block;
            position: relative;
            cursor: pointer;

            img {
                width: 100%;
                height: 100%;
            }

            .btndel {
                position: absolute;
                z-index: 1;
                width: 20px !important;
                height: 20px !important;
                left: 46px;
                top: -4px;
            }
        }
    }
    .iview-video-style {
        width: 40%;
        height: 180px;
        border-radius: 10px;
        background-color: #707070;
        margin-top: 10px;
        position: relative;
        overflow: hidden;
        .video-style {
            width: 100%;
            height: 100% !important;
            border-radius: 10px;
        }
        .iconv{
            color: #fff;
            line-height: 180px;
            width: 50px;
            height: 50px;
            display: inherit;
            font-size: 26px;
            position: absolute;
            top: -74px;
            left: 50%;
            margin-left: -25px;
        }
        .mark{
            position: absolute;
            width: 100%;
            height: 30px;
            top: 0;
            background-color: rgba(0, 0, 0, 0.5);
            text-align: center;
        }
    }
    .imgPic{
        .info{
            width: 60%;
            margin-left: 10px;
        }
        .pictrue{
            height: 36px;
            margin: 7px 3px 0 3px;
            img{
                height: 100%;
                display: block;
            }
        }
    }
    .margins{
        margin: 0 35px;
    }
}
/deep/.ivu-tabs-ink-bar{
    display: none;
}
/deep/.ivu-tabs-bar{
    background: #F5F7FA;
    border-bottom: 0;
    margin-bottom: 0;
}
/deep/.ivu-tabs-nav-wrap {
    margin-bottom: 0;
}
/deep/.ivu-tabs-nav{
    height: 40px;
    line-height: 40px;
}
/deep/.ivu-tabs-nav .ivu-tabs-tab-active{
    color: rgba(0,0,0,0.85);
    font-weight: 400;
    background-color: #fff;
    &::before {
        content: '';
        position: absolute;
        top: 0;
        left: 0;
        width: 100%;
        height: 2px;
        background-color: #1890FF;
    }
}
/deep/.ivu-tabs-tabpane {
    padding: 15px;

    &:first-child {
        padding: 0 25px;
    }
}
/deep/.ivu-tabs-nav .ivu-tabs-tab{
    padding: 7px 19px !important;
    margin-right: 0;
    line-height: 26px;
}
/deep/.ivu-tabs-nav-container {
    font-size: 13px;
}
