import { mapState, mapMutations } from 'vuex';
import { levelListApi, setShowApi, createApi } from '@/api/user';
import taskList from './handle/task';
import editFrom from '@/components/from/from';
export default {
    name: 'user_level',
    components: { editFrom, taskList },
    data () {
        return {
            grid: {
                xl: 7,
                lg: 7,
                md: 12,
                sm: 24,
                xs: 24
            },
            loading: false,
            columns1: [
                {
                    title: 'ID',
                    key: 'id',
                    width: 80
                },
                {
                    title: '等级图标',
                    slot: 'icons',
                    minWidth: 100
                },
                {
                    title: '等级名称',
                    key: 'name',
                    minWidth: 120
                },
                {
                    title: '等级',
                    key: 'grade',
                    minWidth: 100
                },
                {
                    title: '享受折扣',
                    key: 'discount',
                    minWidth: 100
                },
                // {
                //     title: '有效时间',
                //     key: 'valid_date',
                //     minWidth: 120
                // },
                // {
                //     title: '是否永久',
                //     slot: 'is_forevers',
                //     minWidth: 130
                // },
                // {
                //     title: '是否付费',
                //     slot: 'is_pays',
                //     minWidth: 120
                // },
                {
                    title: '是否显示',
                    slot: 'is_shows',
                    minWidth: 120
                },
                {
                    title: '等级说明',
                    key: 'explain',
                    minWidth: 120
                },
                {
                    title: '操作',
                    slot: 'action',
                    fixed: 'right',
                    minWidth: 120
                }
            ],
            levelFrom: {
                is_show: '',
                title: '',
                page: 1,
                limit: 10
            },
            levelLists: [],
            total: 0,
            FromData: null,
            imgName: '',
            visible: false,
            levelId: 0,
            modalTitleSs: '',
            titleType: 'level',
            modelTask: false,
            num: 0
        }
    },
    created () {
        this.getList();
    },
    computed: {
        ...mapState('admin/layout', [
            'isMobile'
        ]),
        labelWidth () {
            return this.isMobile ? undefined : 96;
        },
        labelPosition () {
            return this.isMobile ? 'top' : 'right';
        }
    },
    methods: {
        ...mapMutations('admin/userLevel', [
            'getlevelId'
        ]),
        // 操作
        changeMenu (row, name, num) {
            this.levelId = row.id;
            switch (name) {
            case '1':
                this.getlevelId(this.levelId);
                this.$refs.tasks.modals = true;
                this.$refs.tasks.getList();
                break;
            default:
                this.del(row, '删除等级', num);
            }
        },
        // 删除
        del (row, tit, num) {
            let delfromData = {
                title: tit,
                num: num,
                url: `user/user_level/delete/${row.id}`,
                method: 'put',
                ids: ''
            }
            this.$modalSure(delfromData).then((res) => {
                this.$Message.success(res.msg);
                this.levelLists.splice(num, 1);
            }).catch(res => {
                this.$Message.error(res.msg);
            });
        },
        // 删除成功
        // submitModel () {
        //     this.levelLists.splice(this.delfromData.num, 1)
        // },
        // 修改是否显示
        onchangeIsShow (row) {
            let data = {
                id: row.id,
                is_show: row.is_show
            }
            setShowApi(data).then(async res => {
                this.$Message.success(res.msg);
            }).catch(res => {
                this.$Message.error(res.msg);
            })
        },
        // 等级列表
        getList () {
            this.loading = true;
            this.levelFrom.is_show = this.levelFrom.is_show || '';
            levelListApi(this.levelFrom).then(async res => {
                let data = res.data
                this.levelLists = data.list;
                this.total = res.data.count;
                this.loading = false;
            }).catch(res => {
                this.loading = false;
                this.$Message.error(res.msg);
            })
        },
        pageChange (index) {
            this.levelFrom.page = index;
            this.getList();
        },
        // 添加
        add () {
            this.levelId = 0;
            this.$modalForm(createApi({ id: this.levelId })).then(() => this.getList());
        },
        // 编辑
        edit (row) {
            this.levelId = row.id;
            this.$modalForm(createApi({ id: this.levelId })).then(() => this.getList());
            this.getlevelId(this.levelId);
        },
        // 表格搜索
        userSearchs () {
            this.levelFrom.page = 1;
            this.getList();
        },
        // 修改成功
        submitFail () {
            this.getList();
        }
    }
}
