import { verifyOrderApi, merchantStoreListApi } from '@/api/setting';
import cardsData from '@/components/cards/cards';
import referrerInfo from '@/components/referrerInfo/index';
export default {
    name: 'setting_order',
    components: { cardsData, referrerInfo },
    data () {
        return {
            formValidate: {
                page: 1,
                limit: 15,
                data: '',
                real_name: '',
                store_id: '',
                field_key: ''
            },
            fromList: {
                title: '选择时间',
                custom: true,
                fromTxt: [
                    { text: '全部', val: '' },
                    { text: '今天', val: 'today' },
                    { text: '昨天', val: 'yesterday' },
                    { text: '最近7天', val: 'lately7' },
                    { text: '最近30天', val: 'lately30' },
                    { text: '本月', val: 'month' },
                    { text: '本年', val: 'year' }
                ]
            },
            timeVal: [],
            storeSelectList: [],
            cardLists: [],
            columns: [
                {
                    title: '订单号',
                    key: 'order_id',
                    minWidth: 180,
                    sortable: true
                },
                {
                    title: '用户信息',
                    slot: 'name',
                    minWidth: 120
                },
                {
                    title: '推荐人信息',
                    slot: 'spread_nickname',
                    minWidth: 120
                },
                {
                    title: '商品信息',
                    slot: 'info',
                    minWidth: 360
                },
                {
                    title: '实际支付',
                    key: 'pay_price',
                    minWidth: 90
                },
                {
                    title: '核销员',
                    key: 'clerk_name',
                    minWidth: 90
                },
                {
                    title: '核销门店',
                    key: 'store_name',
                    minWidth: 120
                },
                {
                    title: '支付状态',
                    key: 'pay_type_name',
                    minWidth: 80
                },
                {
                    title: '订单状态',
                    slot: 'status_name',
                    minWidth: 80
                },
                {
                    title: '下单时间',
                    key: 'add_time',
                    minWidth: 130,
                    sortable: true
                }
            ],
            orderList: [],
            loading: false,
            total: 0
        }
    },
    mounted () {
        this.getList();
        this.storeList();
    },
    methods: {
        getList () {
            let that = this;
            that.loading = true;
            verifyOrderApi(that.formValidate).then(res => {
                that.loading = false;
                that.orderList = res.data.data;
                that.total = res.data.count;
                that.cardLists = res.data.badge;
            }).catch(res => {
                that.$Message.error(res.msg);
            })
        },
        userSearchs () {
            this.formValidate.page = 1;
            this.getList();
        },
        // 具体日期
        onchangeTime (e) {
            this.timeVal = e;
            this.formValidate.data = this.timeVal.join('-');
            if(this.formValidate.data == '-'){
                this.formValidate.data = ''
            }
        },
        storeList () {
            let that = this;
            merchantStoreListApi().then(res => {
                that.storeSelectList = res.data
            }).catch(res => {
                that.$Message.error(res.msg);
            })
        },
        pageChange (index) {
            this.formValidate.page = index;
            this.getList();
        },
        referenceInfo (uid) {
            this.$refs.info.isTemplate = true;
            this.$refs.info.verifySpreadInfo(uid);
        },
        refresh () {
            this.formValidate = {
                page: 1,
                limit: 15,
                data: '',
                real_name: '',
                store_id: '',
                field_key:''
            };
            // this.field_key = '';
            this.getList();
        },
        radioChange (e) {
            this.formValidate.data = e;
            this.getList();
        }

    }
}
