import toolCom from '@/components/mobileConfigRight/index.js'
import rightBtn from '@/components/rightBtn/index.vue';
export default {
    name: 'c_picture_cube',
    componentsName: 'picture_cube',
    cname: '图片魔方',
    props: {
        activeIndex: {
            type: null
        },
        num: {
            type: null
        },
        index: {
            type: null
        }
    },
    components: {
        ...toolCom,
        rightBtn
    },
    data () {
        return {
            configObj: {},
            rCom: [
                {
                    components: toolCom.c_tab,
                    configNme: 'tabConfig'
                },
                {
                    components: toolCom.c_pictrue,
                    configNme: 'picStyle'
                },
                {
                    components: toolCom.c_menu_list,
                    configNme: 'menuConfig'
                },
                {
                    components: toolCom.c_bg_color,
                    configNme: 'bgColor'
                },
                {
                    components: toolCom.c_txt_tab,
                    configNme: 'bgStyle'
                },
                {
                    components: toolCom.c_slider,
                    configNme: 'prConfig'
                },
                {
                    components: toolCom.c_slider,
                    configNme: 'mbConfig'
                }
            ]
        }
    },
    watch: {
        num (nVal) {
            let value = JSON.parse(JSON.stringify(this.$store.state.admin.mobildConfig.defaultArray[nVal]))
            this.configObj = value;
        },
        configObj: {
            handler (nVal, oVal) {
                this.$store.commit('admin/mobildConfig/UPDATEARR', { num: this.num, val: nVal });
            },
            deep: true
        }
    },
    mounted () {
        this.$nextTick(() => {
            let value = JSON.parse(JSON.stringify(this.$store.state.admin.mobildConfig.defaultArray[this.num]))
            this.configObj = value;
        })
    },
    methods: {
    }
}
