.head-box{
    position relative
    display flex
    align-items center
    justify-content center
    color #fff
    height 45px
    background: linear-gradient(85deg, #3875EA 0%, #1890FC 100%);
    span{
        position absolute
        width 45px
        height 100%
        left 0
        top 0
        text-align center
        line-height 45px
    }
}
.chat-box {
    display: flex;
    flex-direction: column;
    height: 100%;
    height: 100vh;
    background #F0F1F2
    .head-box {
        background: linear-gradient(85deg, #3875EA 0%, #1890FC 100%);

        .title-hd {
            display: flex;
            align-items: center;
            justify-content: center;
            position: relative;
            height: 43px;
            padding: 0 .3rem;
            color: #fff;

            .icon-fanhui {
                position: absolute;
                left: .3rem;
                top: 50%;
                transform: translateY(-50%);
            }

            .icon-gengduo2 {
                /* #ifdef MP */
                position: absolute;
                right: 2.1rem;
                top: 50%;
                transform: translateY(-50%);
                /* #endif */
            }
        }
    }

    .scroll-box {
        flex: 1;
    }

    .footer-box {
        display: flex;
        align-items: center;
        height: 1rem;
        padding: 0 .3rem;
        color: rgba(0,0,0,0.8);
        background: #f7f7f7;
        .words{
            margin-right .18rem
        }
        .words .iconfont {
            font-size: .5rem
        }

        .input-box {
            display: flex;
            align-items: center;
            flex 1
            height: .64rem;
            padding-right: 0.05rem;
            background-color: #fff;
            border-radius: .32rem;
            overflow hidden

            input {
                flex: 1;
                padding-left: .2rem;
                height: 100%;
                border transparent !important

            }
            >>> .ivu-input{
                font-size .28rem !important
            }
            >>> .ivu-input, .ivu-input:hover, .ivu-input:focus {
                border transparent
                box-shadow: none;
            }

            .iconfont {
                font-size: .5rem;
                color: #ccc;
                font-weight: normal;
            }

            .isSend {
                color: #3875EA;
            }
        }

        .emoji .iconfont {
            margin-left: .18rem;
            font-size: .5rem;
        }

        .more .iconfont {
            margin-left: .18rem;
            font-size: .5rem;
        }

    }
}

.tool-wrapper {
    display: flex;
    justify-content: space-between;
    padding: .45rem .6rem;
    background: #fff;
    font-size: .24rem;

    .tool-item {
        text-align: center;

        img {
            width: 1.04rem;
            height: 1.04rem;
        }
    }
}

.slider-banner {
    padding-bottom .25rem
    background: #fff;

    .em {
        display: inline-block;
        width: .5rem;
        height: .5rem;
        margin: .4rem 0 0 .5rem;
    }
}

.words-mask {
    z-index: 50;
    position: fixed;
    left: 0;
    top: 0;
    right: 0;
    bottom: 0;
    background: rgba(0, 0, 0, 0.5);

    .content {
        position: absolute;
        left: 0;
        right: 0;
        top: 1.14rem;
        bottom: 0;
        display: flex;
        flex-direction: column;
        background: #fff;
        border-radius: 0.06rem 0.06rem 0px 0px;

        .title-box {
            padding: 0 .3rem .3rem;
            position: relative;
            border-bottom 1px solid #F5F6F9
            .tab-box{
                position relative
                display flex
                justify-content space-between
                padding .4rem 2.2rem .3rem
                font-size .32rem
                color #9F9F9F
                .on{
                    color #3875EA
                    font-weight bold
                }
                .right-icon{
                    position absolute
                    right 0
                    top 50%
                    transform translateY(-50%)
                    .iconfont{
                        margin-left .2rem
                        font-size .48rem
                        color #C8CAD0
                    }
                }
            }
            .input-box{
                display: flex;
                align-items: center;
                width: 6.9rem;
                height: .64rem;
                padding-right: 0.05rem;
                margin-left: .18rem;
                border-radius: .32rem;
                overflow hidden

                >>> .ivu-input{
                    background #F5F6F9
                }
                >>> .ivu-input, .ivu-input:hover, .ivu-input:focus {
                    border transparent
                    box-shadow: none;
                }
            }
            .icon-cha1 {
                position: absolute;
                right: 0;
                top: 50%;
                transform: translateY(-50%);
            }
        }

        .scroll-box {
            flex: 1;
            display flex
            overflow: hidden;
            .scroll-left{
                width 1.76rem
                height 100%
                overflow-y scroll
                -webkit-overflow-scrolling touch
                background #F5F6F9
                .left-item{
                    position relative
                    display flex
                    align-items center
                    justify-content center
                    width 100%
                    height 1.09rem
                    color #282828
                    font-size .26rem
                    &.active{
                        color #3875EA
                        background #fff
                        &:after{
                            content ' '
                            position: absolute;
                            left 0
                            top 50%
                            transform translateY(-50%)
                            width 0.06rem
                            height .46rem
                            background #3875EA
                        }
                    }
                    &.add_cate{
                        color #9F9F9F
                        font-size .26rem
                        .iconfont{
                            margin-right 0.1rem
                            font-size .24rem
                        }
                    }
                }
            }
            .right-box{
                flex 1
                overflow scroll
                -webkit-overflow-scrolling touch
            }
            .msg-item {
                padding: .25rem .3rem;
                color #888888
                font-size .28rem
                .title{
                    margin-right .2rem
                    color #282828
                }
                &.add-mg{
                    display flex
                    align-items center
                    justify-content flex-end
                    font-size .28rem
                    padding .15rem .3rem
                    .iconfont{
                        font-size .36rem
                        margin-right .1rem
                    }
                }
            }
        }
    }
}

.chat-scroll-box {
    flex: 1;
    overflow: hidden;
    .day-box{
        margin-bottom .2rem
        font-size .24rem
        color #999
        text-align: center;
    }
    .chat-item {
        display: flex;
        margin-bottom: .36rem;
        font-size .28rem
        .avatar {
            width: .8rem;
            height: .8rem;
            border-radius: 50%;
        }

        .msg-box {
            display: flex;
            align-items: center;
            max-width: 4.52rem;
            margin-left: .22rem;
            padding: .1rem .24rem;
            background: #fff;
            border-radius: .14rem;
            word-break: break-all;
            font-size .28rem
        }

        .img-box {
            width: 2.7rem;
            margin-left: .22rem;

            img {
                width: 2.7rem;
                border-radius 6px
            }
        }

        .product-box {
            width: 4.52rem;
            background-color: #fff;
            border-radius: .14rem;
            overflow: hidden;
            margin-left: .22rem;

            img {
                width: 4.52rem;
            }

            .info {
                padding: .16rem .26rem;

                .price {
                    font-size: .36rem;
                    color: #F74C31;

                    text {
                        font-size: .28rem;
                    }
                }
            }
        }

        .order-box {
            width: 4.52rem;
            margin-left: .22rem;
            background-color: #fff;
            border-radius: .14rem;

            .title {
                padding: .15rem .2rem;
                font-size: .26rem;
                color: #282828;
                border-bottom: 1px solid #ECEFF8;
            }

            .info {
                display: flex;
                padding: .2rem;

                img {
                    width: 1.24rem;
                    height: 1.24rem;
                    border-radius: 0.06rem;
                }

                .product-info {
                    flex: 1;
                    display: flex;
                    flex-direction: column;
                    justify-content: space-between;
                    margin-left: .16rem;

                    .name {
                        font-size: .26rem;
                    }

                    .price {
                        font-size: .3rem;
                        color: #F74C31;
                    }
                }
            }
        }

        &.right-box {
            flex-direction: row-reverse;

            .msg-box {
                margin-left: 0;
                margin-right: .22rem;
                background-color: #9cec60;

                color #333
            }

            .img-box {
                margin-left: 0;
                margin-right: .22rem;
            }

            .product-box {
                margin-left: 0;
                margin-right: .22rem;
            }

            .order-box {
                margin-left: 0;
                margin-right: .22rem;
            }
        }

        .em {
            margin: 0;
        }
    }
}
.transfer-mask
    z-index 30
    position fixed
    left 0
    top 0
    width 100%
    height 100%
    background rgba(0,0,0,0.5)
    .content
        position absolute
        left 0
        bottom 0
        transform translateY(100%)
        top 2.5rem
        right 0
        display flex
        flex-direction column
        background #fff
        border-radius: .16rem .16rem 0px 0px;
        &.on
            animation up .2s linear
            animation-fill-mode: forwards;
        .title
            position relative
            display flex
            align-items center
            justify-content center
            height 1.1rem
            font-size .32rem
            font-weight bold
            color #282828
            .iconfont
                position absolute
                right .3rem
                top 50%
                transform translateY(-50%)
                color #C8CAD0
                font-size .44rem
        .list-wrapper
            flex 1
            padding-left .3rem
            overflow-y scroll
            -webkit-overflow-scrolling touch
            .list-item
                display flex
                align-items center
                padding .16rem 0
                border-bottom 1px solid #ECEFF8
                .check-box
                    width .72rem
                .avatar-box img
                    width .9rem
                    height .9rem
                    border-radius 0.06rem
                .nickName
                    margin-left .28rem
                    color #282828
                    font-size .3rem
                    font-weight bold
        .btn
            width 6.9rem
            height .86rem
            margin .5rem auto
            color #fff
            background #3875EA
            font-size .3rem
            border-radius: .43rem !important;
.broadcast-details_box {
    display: flex;
    background: #fff;
    border-radius: 6px;
    padding: .24rem;
}

.broadcast_details_img {
    width: 1.4rem;
    height: 1.4rem;
    border-radius: 8px;
    overflow: hidden;
    position: relative;
}

.broadcast_details_img img {
    width: 100%;
    height: 100%;
}

.broadcast_details_picBox {
    width: 75%;
    margin-left: .24rem;
}

.broadcast_details_tit {
    font-size: .28rem;
    color: #333333;
    height: .85rem;
    font-weight: 800;
    overflow: hidden;
    text-overflow: ellipsis;
    display: -webkit-box;
    -webkit-line-clamp: 2;
    -webkit-box-orient: vertical;
    text-align: left !important;
}

.broadcast_details_pic {
    font-size: .36rem;
    color: #e93323;
    text-align: left;
}

.broadcast_details_pic_num {
    text-decoration: line-through;
    font-size: .28rem;
    color: rgba(0, 0, 0, 0.5);
    margin-left: 0.1rem;
}

.broadcast_details_btn {
    width: 1.3rem;
    height: .5rem;
    background: #e83323;
    opacity: 1;
    border-radius: 1.25rem;
    color: #fff;
    font-size: .24rem;
    text-align: center;
    line-height: .5rem;
}

.broadcast-details_num {
    width: 100%;
    height: .8rem;
    line-height: .8rem;
    color: #000000;
    font-size: .26rem;
    display: flex;
    justify-content: space-between;
    background: #fff;
    border-bottom: 1px dashed rgba(0, 0, 0, 0.2);
    padding: 0 .24rem;
}
