import { mapState } from 'vuex';
import { templatesApi } from '@/api/setting';
import freightTemplate from '@/components/freightTemplate/index';

export default {
    name: 'setting_templates',
    components: { freightTemplate },
    data () {
        return {
            grid: {
                xl: 7,
                lg: 7,
                md: 12,
                sm: 24,
                xs: 24
            },
            loading: false,
            columns1: [
                {
                    title: 'ID',
                    key: 'id',
                    width: 80
                },
                {
                    title: '模板名称',
                    key: 'name',
                    minWidth: 100
                },
                {
                    title: '计费方式',
                    key: 'type',
                    minWidth: 120
                },
                {
                    title: '指定包邮',
                    key: 'appoint',
                    minWidth: 120
                },
                {
                    title: '排序',
                    key: 'sort',
                    minWidth: 120
                },
                {
                    title: '添加时间',
                    key: 'add_time',
                    minWidth: 120
                },
                {
                    title: '操作',
                    slot: 'action',
                    fixed: 'right',
                    minWidth: 120
                }
            ],
            levelFrom: {
                name: '',
                page: 1,
                limit: 15
            },
            levelLists: [],
            total: 0,
            FromData: null
        }
    },
    created () {
        this.getList();
    },
    computed: {
        ...mapState('admin/layout', [
            'isMobile'
        ]),
        labelWidth () {
            return this.isMobile ? undefined : 75;
        },
        labelPosition () {
            return this.isMobile ? 'top' : 'right';
        }
    },
    methods: {
        // 添加运费模板
        freight () {
            this.$refs.template.id = 0;
            this.$refs.template.isTemplate = true;
        },
        // 删除
        del (row, tit, num) {
            let delfromData = {
                title: tit,
                num: num,
                url: `setting/shipping_templates/del/${row.id}`,
                method: 'DELETE',
                ids: ''
            };
            this.$modalSure(delfromData).then((res) => {
                this.$Message.success(res.msg);
                this.levelLists.splice(num, 1);
            }).catch(res => {
                this.$Message.error(res.msg);
            });
        },
        // 运费模板列表
        getList () {
            this.loading = true;
            templatesApi(this.levelFrom).then(async res => {
                let data = res.data;
                this.levelLists = data.data;
                this.total = data.count;
                this.loading = false;
            }).catch(res => {
                this.loading = false;
                this.$Message.error(res.msg);
            })
        },
        pageChange (index) {
            this.levelFrom.page = index;
            this.getList();
        },
        // 编辑
        edit (id) {
            this.$refs.template.isTemplate = true;
            this.$refs.template.editFrom(id);
        },
        // 表格搜索
        userSearchs () {
            this.levelFrom.page = 1;
            this.getList();
        }
    }
}
