import { mapState, mapMutations } from 'vuex';
import { categoryAddApi, categoryEditApi, categoryListApi, statusApi } from '@/api/cms';
export default {
    name: 'articleCategory',
    data () {
        return {
            grid: {
                xl: 7,
                lg: 7,
                md: 12,
                sm: 24,
                xs: 24
            },
            loading: false,
            formValidate: {
                status: '',
                page: 1,
                limit: 20,
                type: 0
            },
            total: 0,
            columns1: [
                {
                    title: 'ID',
                    key: 'id',
                    width: 80
                },
                {
                    title: '分类昵称',
                    key: 'title',
                    minWidth: 130
                },
                {
                    title: '分类图片',
                    slot: 'images',
                    minWidth: 130
                },
                {
                    title: '状态',
                    slot: 'statuss',
                    minWidth: 130
                },
                {
                    title: '操作',
                    slot: 'action',
                    fixed: 'right',
                    minWidth: 120
                }
            ],
            FromData: null,
            modalTitleSs: '',
            categoryId: 0,
            categoryList: []
        }
    },
    computed: {
        ...mapState('admin/layout', [
            'isMobile'
        ]),
        labelWidth () {
            return this.isMobile ? undefined : 96;
        },
        labelPosition () {
            return this.isMobile ? 'top' : 'right';
        }
    },
    mounted () {
        this.getList();
    },
    methods: {
        ...mapMutations('admin/userLevel', [
            'getCategoryId'
        ]),
        // 添加
        add () {
            this.$modalForm(categoryAddApi()).then(() => this.getList());
            // categoryAddApi().then(async res => {
            //     this.$refs.edits.modals = true;
            //     this.FromData = res.data;
            // }).catch(res => {
            //     this.$Message.error(res.msg);
            // })
        },
        // 编辑
        edit (row) {
            this.$modalForm(categoryEditApi(row.id)).then(() => this.getList());
            // categoryEditApi(row.id).then(async res => {
            //     this.FromData = res.data;
            //     this.$refs.edits.modals = true;
            // }).catch(res => {
            //     this.$Message.error(res.msg);
            // })
        },
        // 删除
        del (row, tit) {
            let delfromData = {
                title: tit,
                num: 0,
                url: `cms/category/${row.id}`,
                method: 'DELETE',
                ids: ''
            }
            this.$modalSure(delfromData).then((res) => {
                this.$Message.success(res.msg);
                this.getList();
            }).catch(res => {
                this.$Message.error(res.msg);
            });
        },
        // 列表
        getList () {
            this.loading = true;
            categoryListApi(this.formValidate).then(async res => {
                let data = res.data
                this.categoryList = data.list;
                this.total = data.count;
                this.loading = false;
            }).catch(res => {
                this.loading = false;
                this.$Message.error(res.msg);
            })
        },
        pageChange (index) {
            this.formValidate.page = index;
            this.getList();
        },
        // 表格搜索
        userSearchs () {
            this.formValidate.page = 1;
            this.getList();
        },
        // 修改是否显示
        onchangeIsShow (row) {
            let data = {
                id: row.id,
                status: row.status
            }
            statusApi(data).then(async res => {
                this.$Message.success(res.msg);
            }).catch(res => {
                this.$Message.error(res.msg);
            })
        },
        // 查看文章
        lookUp (row) {
            this.$router.push({ path: '/admin/cms/article/index',
                                query: {
                                    id: row.id
            } });
            // xia mian chu cun mei yong;
            this.getCategoryId(row.id)
        }
    }
}
