    import { userLabel,userLabelPut } from '@/api/kefu'
    export default {
        name: "userLabel",
        props:{
            uid:{
                type:String | Number,
                default:''
            }
        },
        data(){
            return {
							isUser:false,
              labelList:[],
              activeIds:[]
            }
        },
        mounted() {
            this.getList()
        },
        methods:{
            getList(){
                userLabel(this.uid).then(res=>{
                    res.data.map(el=>{
											  this.isUser = true;
                        el.label.map(label=>{
                            if(label.disabled){
                                this.activeIds.push(label.id)
                            }
                        })
                    })
                    this.labelList =res.data
                })
            },
            selectLabel(label){
                if(label.disabled){
                    let index = this.activeIds.indexOf(label.id)
                    this.activeIds.splice(index,1)
                    label.disabled = false
                }else{
                    this.activeIds.push(label.id)
                    label.disabled = true
                }
            },
            // 确定
            subBtn(){
                let unLaberids = [];
                this.labelList.map(item=>{
                    item.label.map( i => {
                        if(i.disabled == false){
                            unLaberids.push(i.id);
                        }
                    });
                });
                userLabelPut(this.uid,{
                    label_ids: this.activeIds,
                    un_label_ids: unLaberids
                }).then(res=>{
                    this.$Message.success(res.msg)
                    this.$emit('editLabel')
                }).catch(error=>{
                    this.$Message.error(error.msg)
                })
            },
            cancel(){
                this.$emit('close')
            }
        }
    }
