import { mapState } from "vuex";
import { commissionListApi, userCommissionApi } from "@/api/finance";
import commissionDetails from "./handle/commissionDetails";
import exportExcel from "@/utils/newToExcel.js";
import timeOptions from "@/utils/timeOptions";
export default {
  name: "commissionRecord",
  components: { commissionDetails },
  data() {
    return {
      timeVal: [], //时间筛选
      total: 0,
      loading: false,
      tabList: [],
      formValidate: {
        nickname: "",
        price_max: "",
        price_min: "",
        excel: 0,
        page: 1, // 当前页
        limit: 20, // 每页显示条数
      },
      options:timeOptions,
      columns: [
        {
          title: "昵称/姓名/ID",
          slot: "nickname",
          minWidth: 230,
        },
        {
          title: "总佣金金额",
          key: "sum_number",
          // sortable: "custom",
          minWidth: 120,
        },
        {
          title: "账户余额",
          key: "now_money",
          minWidth: 100,
        },
        {
          title: "账户佣金",
          key: "brokerage_price",
          // sortable: "custom",
          minWidth: 120,
        },
        {
          title: "提现到账佣金",
          key: "extract_price",
          minWidth: 150,
        },
        {
          title: "时间",
          key: "time",
          minWidth: 150,
        },
        {
          title: "操作",
          slot: "action",
          // fixed: "right",
          minWidth: 100,
        },
      ],
    };
  },
  computed: {
    ...mapState("admin/layout", ["isMobile"]),
    labelWidth() {
      return this.isMobile ? undefined : 96;
    },
    labelPosition() {
      return this.isMobile ? "top" : "right";
    },
  },
  mounted() {
    this.getList();
  },
  methods: {
    // 列表
    getList() {
      this.loading = true;
      commissionListApi(this.formValidate)
        .then(async (res) => {
          let data = res.data;
          this.tabList = data.list;
          this.total = data.count;
          this.loading = false;
        })
        .catch((res) => {
          this.loading = false;
          this.$Message.error(res.msg);
        });
    },
    // 选择时间
    selectChange(tab) {
      this.formValidate.page = 1;
      this.formValidate.date = tab;
      this.timeVal = [];
      this.getList();
    },
    // 具体日期
    onchangeTime(e) {
      this.timeVal = e;
      this.formValidate.date = this.timeVal.join("-");
      this.formValidate.page = 1;
      if (!e[0]) {
        this.formValidate.date = "";
      }
      this.getList();
    },
    pageChange(index) {
      this.formValidate.page = index;
      this.getList();
    },
    // 搜索
    userSearchs() {
      this.formValidate.page = 1;
      this.getList();
    },
    // 导出
    // exports() {
    //   let formValidate = this.formValidate;
    //   let data = {
    //     price_max: formValidate.price_max,
    //     price_min: formValidate.price_min,
    //     nickname: formValidate.nickname,
    //   };
    //   userCommissionApi(data)
    //     .then((res) => {
    //       location.href = res.data[0];
    //     })
    //     .catch((res) => {
    //       this.$Message.error(res.msg);
    //     });
    // },
    // 数据导出；
    async exports() {
      let [th, filekey, data, fileName] = [[], [], [], ""];
      //   let fileName = "";
      let excelData = JSON.parse(JSON.stringify(this.formValidate));
      excelData.page = 1;
      for (let i = 0; i < excelData.page + 1; i++) {
        let lebData = await this.getExcelData(excelData);
        if (!fileName) fileName = lebData.filename;
        if (!filekey.length) {
          filekey = lebData.filekey;
        }
        if (!th.length) th = lebData.header;
        if (lebData.export.length) {
          data = data.concat(lebData.export);
          excelData.page++;
        } else {
          exportExcel(th, filekey, fileName, data);
          return;
        }
      }
    },
    getExcelData(excelData) {
      return new Promise((resolve, reject) => {
        userCommissionApi(excelData).then((res) => {
          return resolve(res.data);
        });
      });
    },
    // 详情
    Info(row) {
      this.$refs.commission.modals = true;
      this.$refs.commission.getDetails(row.uid);
      this.$refs.commission.getList(row.uid);
    },
    // 排序
    sortChanged(e) {
      if (e.key == "sum_number") {
        delete this.formValidate.brokerage_price;
      } else {
        delete this.formValidate.sum_number;
      }
      this.formValidate[e.key] = e.order;
      this.getList();
    },
  },
};
