import { storeApi, keyApi, storeAddApi, storeGetInfoApi } from '@/api/setting';
import { mapState } from 'vuex';
import city from '@/utils/city';
import uploadPictures from '@/components/uploadPictures';
export default {
    name: 'systemStore',
    components: { uploadPictures },
    props: { },
    data () {
        const validatePhone = (rule, value, callback) => {
            if (!value) {
                return callback(new Error('请填写手机号'));
            } else if (!/^1[3456789]\d{9}$/.test(value)) {
                callback(new Error('手机号格式不正确!'));
            } else {
                callback();
            }
        };
        const validateUpload = (rule, value, callback) => {
            if (!this.formItem.image) {
                callback(new Error('请上传提货点logo'))
            } else {
                callback()
            }
        };
        return {
            isTemplate: false,
            spinShow: false,
            modalMap: false,
            addresData: [],
            formItem: {
                name: '',
                introduction: '',
                phone: '',
                address: [],
                address2: [],
                detailed_address: '',
                valid_time: [],
                day_time: [],
                latlng: '',
                id: 0
            },
            ruleValidate: {
                name: [
                    { required: true, message: '请输入提货点名称', trigger: 'blur' }
                ],
                mail: [
                    { required: true, message: 'Mailbox cannot be empty', trigger: 'blur' },
                    { type: 'email', message: 'Incorrect email format', trigger: 'blur' }
                ],
                address: [
                    { required: true, message: '请选择提货点地址', type: 'array', trigger: 'change' }
                ],
                valid_time: [
                    {
                        required: true,
                        type: 'array',
                        message: '请选择核销时效',
                        trigger: 'change',
                        fields: {
                            0: { type: 'date', required: true, message: '请选择年度范围' },
                            1: { type: 'date', required: true, message: '请选择年度范围' }
                        }
                    }

                ],
                day_time: [
                    { required: true, type: 'array', message: '请选择提货点营业时间', trigger: 'change' }
                ],
                phone: [
                    { required: true, validator: validatePhone, trigger: 'blur' }
                ],
                detailed_address: [
                    { required: true, message: '请输入详细地址', trigger: 'blur' }
                ],
                image: [
                    { required: true, validator: validateUpload, trigger: 'change' }
                ],
                latlng: [
                    { required: true, message: '请选择经纬度', trigger: 'blur' }
                ]
            },
            keyUrl: '',
            grid: {
                xl: 20,
                lg: 20,
                md: 20,
                sm: 24,
                xs: 24
            },
            gridPic: {
                xl: 6,
                lg: 8,
                md: 12,
                sm: 12,
                xs: 12
            },
            gridBtn: {
                xl: 4,
                lg: 8,
                md: 8,
                sm: 8,
                xs: 8
            },
            modalPic: false,
            isChoice: '单选'
        }
    },
    created () {
        let that = this;
        city.map((item) => {
            item.value = item.label;
            if (item.children && item.children.length) {
                item.children.map((j) => {
                    j.value = j.label;
                    if (j.children && j.children.length) {
                        j.children.map((o) => {
                            o.value = o.label;
                        });
                    }
                });
            }
        });
        setTimeout(function () {
            that.addresData = city;
        }, 10);
    },
    computed: {
        ...mapState('admin/layout', [
            'isMobile'
        ]),
        labelWidth () {
            return this.isMobile ? undefined : 120;
        },
        labelPosition () {
            return this.isMobile ? 'top' : 'right';
        }
    },
    mounted: function () {
        window.addEventListener('message', function (event) {
            // 接收位置信息，用户选择确认位置点后选点组件会触发该事件，回传用户的位置信息
            var loc = event.data;
            if (loc && loc.module === 'locationPicker') { // 防止其他应用也会向该页面post信息，需判断module是否为'locationPicker'
                window.parent.selectAdderss(loc);
            }
        }, false);
        window.selectAdderss = this.selectAdderss;
    },
    methods: {
        cancel () {
            this.$refs['formItem'].resetFields();
            this.clearFrom();
        },
        clearFrom () {
            this.formItem.introduction = ''
            this.formItem.day_time = []
        },
        // 选择经纬度
        selectAdderss (data) {
            this.formItem.latlng = data.latlng.lat + ',' + data.latlng.lng;
            this.modalMap = false;
        },
        // key值
        getKey () {
            keyApi().then(async res => {
                this.modalMap = true;
                let keys = res.data.key;
                this.keyUrl = `https://apis.map.qq.com/tools/locpicker?type=1&key=${keys}&referer=myapp`;
            }).catch(res => {
                this.$Modal.confirm({
                    title: '提示',
                    content: '<p>'+res.msg+'</p>',
                    onOk: () => {
                        this.$router.push({ path:'/admin/setting/system_config'});
                    },
                    onCancel: () => {
                    }
                });
                // this.$Message.error(res.msg);
            })
        },
        // 详情
        getInfo (id) {
            let that = this;
            that.formItem.id = id;
            that.spinShow = true;
            storeGetInfoApi(id).then(res => {
                let info = res.data.info || null;
                that.formItem = info || that.formItem;
                that.formItem.address = info.address2;
                that.spinShow = false;
            }).catch(function (res) {
                that.spinShow = false;
                that.$Message.error(res.msg);
            })
        },
        // 选择图片
        modalPicTap () {
            this.modalPic = true;
        },
        // 选中图片
        getPic (pc) {
            this.formItem.image = pc.att_dir;
            this.modalPic = false;
        },
        // 选择地址
        handleChange (value, selectedData) {
            this.formItem.address = selectedData.map(o => o.label);
            //  this.formItem.address2 = selectedData.map(o => o.value);
        },
        // 核销时效
        onchangeDate (e) {
            this.formItem.valid_time = e;
        },
        // 营业时间
        onchangeTime (e) {
            this.formItem.day_time = e;
        },
        onSearch () {
            this.getKey();
        },
        // 提交
        handleSubmit (name) {
            this.$refs[name].validate((valid) => {
                if (valid) {
                    storeAddApi(this.formItem).then(async res => {
                        this.$Message.success(res.msg);
                        this.isTemplate = false;
                        this.$parent.getList();
                        this.$refs[name].resetFields();
                        this.clearFrom();
                    }).catch(res => {
                        this.$Message.error(res.msg);
                    })
                } else {
                    return false;
                }
            })
        }
    }
}
