import { mapState } from 'vuex'
export default {
    name: 'home_bargain',
    cname: '砍价',
    icon:'iconkanjia1',
    configName: 'c_home_bargain',
    type:1,// 0 基础组件 1 营销组件 2工具组件
    defaultName:'bargain', // 外面匹配名称
    props: {
        index: {
            type: null
        },
        num: {
            type: null
        }
    },
    computed: {
        ...mapState('admin/mobildConfig', ['defaultArray'])
    },
    watch: {
        pageData: {
            handler (nVal, oVal) {
                this.setConfig(nVal)
            },
            deep: true
        },
        num: {
            handler (nVal, oVal) {
                let data = this.$store.state.admin.mobildConfig.defaultArray[nVal]
                this.setConfig(data)
            },
            deep: true
        },
        'defaultArray': {
            handler (nVal, oVal) {
                let data = this.$store.state.admin.mobildConfig.defaultArray[this.num]
                this.setConfig(data);
            },
            deep: true
        }
    },
    data () {
        return {
            // 默认初始化数据禁止修改
            defaultConfig: {
                name: 'bargain',
                timestamp: this.num,
                setUp: {
                    tabVal: 0
                },
                numConfig: {
                    val: 3
                },
                priceShow: {
                    title: '是否显示价格',
                    val: true
                },
                bntShow: {
                    title: '是否显示按钮',
                    val: true
                },
                themeColor: {
                    title: '主题风格',
                    name: 'themeColor',
                    default: [{
                        item: '#E93323'
                    }],
                    color: [
                        {
                            item: '#E93323'
                        }
                    ]
                },
                titleColor: {
                    title: '标题颜色',
                    name: 'txtColor',
                    default: [{
                        item: '#FF6000'
                    }],
                    color: [
                        {
                            item: '#FF6000'
                        }
                    ]
                },
                fontColor:{
                    title: '价格颜色',
                    name: 'fontColor',
                    default: [
                        {
                            item: '#E93323'
                        }
                    ],
                    color: [
                        {
                            item: '#E93323'
                        }
                    ]
                },
                bgColor: {
                    title: '背景颜色',
                    name: 'bgColor',
                    default: [
                        {
                            item: '#FDDBB2'
                        },
                        {
                            item: '#FDEFC6'
                        }
                    ],
                    color: [
                        {
                            item: '#FDDBB2'
                        },
                        {
                            item: '#FDEFC6'
                        }

                    ]
                },
                bgStyle: {
                    title: '背景样式',
                    name: 'bgStyle',
                    type: 1,
                    list: [
                        {
                            val: '直角',
                            icon: 'iconPic_square'
                        },
                        {
                            val: '圆角',
                            icon: 'iconPic_fillet'
                        }
                    ]
                },
                prConfig: {
                    title: '背景边距',
                    val: 10,
                    min: 0
                },
                productGap: {
                    title: '商品间距',
                    val: 10,
                    min: 0
                },
                mbCongfig: {
                    title: '页面间距',
                    val: 0,
                    min: 0
                }
            },
            fontColor:'',
            bgColor: '',
            mTop: '',
            txtColor: '',
            listRight: '',
            titleColor: '',
            list: [
                {
                    img: '',
                    price: '234'
                },
                {
                    img: '',
                    price: '234'
                },
                {
                    img: '',
                    price: '234'
                }
            ],
            priceShow: true,
            bntShow: true,
            pageData: {},
            bgStyle:1,
            prConfig:0
        }
    },
    mounted () {
        this.$nextTick(() => {
            this.pageData = this.$store.state.admin.mobildConfig.defaultArray[this.num]
            this.setConfig(this.pageData)
        })
    },
    methods: {
        setConfig (data) {
            if(!data) return
            if(data.mbCongfig){
                this.bgColor = data.bgColor.color;
                this.mTop = data.mbCongfig.val;
                this.txtColor = data.themeColor.color[0].item;
                this.listRight = data.productGap.val;
                this.titleColor = data.titleColor.color[0].item;
                this.priceShow = data.priceShow.val;
                this.prConfig = data.prConfig.val;
                this.bgStyle = data.bgStyle.type;
                this.bntShow = data.bntShow.val;
                this.fontColor = data.fontColor.color[0].item;
            }
        }
    }
}
