import build from "./build";
export default {
    name: "inputBuild",
    mixins:[build],
    props:{
        rows: {
            type: Number,
            default: 2
        },
        disabled: {
            type: Boolean,
            default: false
        },
        copyText: {
            type: String,
            default: false
        },
        copy: {
            type: Boolean,
            default: false
        },
        randToken: {
            type: Number,
            default: 0,
        },
        maxlength: {
            type: Number,
            default: 0,
        }
    },
    methods: {
        handleCopySuccess(){
            this.$Message.success('复制成功');
        },
        encodingAESKeyGen() {
             let letters = 'abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789';
             let token = '';
             for(var i = 0; i < 43; i++) {
                var j = parseInt(Math.random() * 61 + 1);
                token += letters[j];
             }
             return token;
        },
        token() {
            let letters = 'abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789';
            let token = '';
            for(var i = 0; i < 32; i++) {
                var j = parseInt(Math.random() * 61 + 1);
                token += letters[j];
            }
            return token;
        },
        randTokenClick() {
            if (this.valueModel) {
                this.$Modal.confirm({
                    title: this.title +'值已存在是否重新生成?',
                    onOk: () => {
                        this.changeToken();
                    }
                });
            } else {
                this.changeToken();
            }
        },
        changeToken(){
            if (this.randToken === 1) {
                this.valueModel = this.token()
                this.$emit('changeValue',{field: this.field, value: this.valueModel});
            } else if (this.randToken === 2) {
                this.valueModel = this.encodingAESKeyGen()
                this.$emit('changeValue',{field: this.field, value: this.valueModel});
            }
        }
    },
}
