import cardsData from "@/components/cards/cards";
import { workMomentInfo,workGroupTemplateSendMsg,workMomentList } from "@/api/work";
 import { formatDate } from '@/utils/validate';
export default {
  data() {
    return {
      cardLists: [],
      optionData: {},
      style: { height: "400px" },
      spinShow: false,
      tableColumn: [
        {
          title: "员工",
          key: "name",
          minWidth: 100,
        },
        {
          title: "发表状态",
          slot: "status",
          minWidth: 100,
        },
        {
          title: "发送时间",
          slot: "create_time",
          minWidth: 100,
        },
        {
          title: "已送达客户",
          slot: "external_user_list",
          minWidth: 150,
        },
        // {
        //   title: "操作",
        //   slot: "action",
        //   minWidth: 100,
        // },
      ],
      tabIndex:0,
      tableData: [],
      userLoading: false,
      timeVal: [],
      tableForm: {
        page: 1,
        limit: 15,
        status:"",
        moment_id:"",
        userid:"",
      },

    };
  },
  filters: {
      formatDate (time) {
          if (time !== 0) {
              let date = new Date(time * 1000);
              return formatDate(date, 'yyyy-MM-dd hh:mm');
          }
      }
  },
  components: { cardsData },
  mounted() {
    this.getData();
    // this.getMemberList();
  },
  methods: {
    getList() {
      this.userLoading = true;
      workMomentList(this.tableForm)
        .then((res) => {
          this.tableData = res.data;
          this.userLoading = false;
        })
        .catch((err) => {
          this.userLoading = false;
          this.$Message.error(err.msg);
        });
    },
    getData() {
      workMomentInfo(this.$route.params.id).then((res) => {
        this.tableForm.moment_id = res.data.moment_id;
        if(!!res.data.moment_id){
          this.getList();
        }
        this.cardLists = [
          {
            col: 6,
            count: res.data.info.externalUserCount,
            name: "已送达客户",
            type:1,
            className: "iconjinrixinzeng",
          },
          {
            col: 6,
            count: res.data.info.userCount,
            name: "全部成员",
            type:1,
            className: "iconjinrituiqun",
          },
          {
            col: 6,
            count: res.data.info.unSendUserCount,
            name: "未发送成员",
            type:1,
            className: "icondangqianqunchengyuan",
          },
          {
            col: 6,
            count: res.data.info.sendUserCount,
            name: "已发送成员",
            type:1,
            className: "iconleijituiqun",
          },
        ];

      });
    },

    sendMsg(row,index){
      workGroupTemplateSendMsg({
					userid:row.msg_id,
					time:row.create_time,
					id:""
				}).then(res=>{
					this.$Message.success(res.msg)
				}).catch(err=>{
					this.$Message.error(err.msg);
				})
    },
    search(){
      this.tableForm.page = 1;
      this.getList();

    },
    pageChange(index) {
      this.tableForm.page = index;
      this.getList();
    },
  },
};
