import { mapState } from 'vuex'
export default {
    name: 'search_box',
    cname: '搜索框',
    icon: 'iconsousukuang1',
    configName: 'c_search_box',
    type:0,// 0 基础组件 1 营销组件 2工具组件
    defaultName:'headerSerch', // 外面匹配名称
    props: {
        index: {
            type: null
        },
        num: {
            type: null
        }
    },
    computed: {
        ...mapState('admin/mobildConfig', ['defaultArray'])
    },
    watch: {
        pageData: {
            handler (nVal, oVal) {
                this.setConfig(nVal)
            },
            deep: true
        },
        num: {
            handler (nVal, oVal) {
                let data = this.$store.state.admin.mobildConfig.defaultArray[nVal]
                this.setConfig(data)
            },
            deep: true
        },
        'defaultArray': {
            handler (nVal, oVal) {
                let data = this.$store.state.admin.mobildConfig.defaultArray[this.num]
                this.setConfig(data);
            },
            deep: true
        }
    },
    data () {
        return {
            // 默认初始化数据禁止修改
            defaultConfig: {
                name: 'headerSerch',
                timestamp: this.num,
                setUp: {
                    tabVal: 0
                },
                tabConfig: {
                    title: '选择模板',
                    tabVal: 2,
                    type: 1,
                    tabList: [
                        {
                            name: '标题',
                            icon: 'iconbiaoti3'
                        },
                        {
                            name: '定位',
                            icon: 'icondingwei1'
                        },
                        {
                            name: '搜索',
                            icon: 'iconsousuo11'
                        }
                    ]
                },
                radioConfig: {
                    title:'样式类型',
                    tabVal: 2,
                    tabList: [
                        {
                            name: '标题'
                        },
                        {
                            name: '定位'
                        },
                        {
                            name: 'logo'
                        }
                    ]
                },
                titleConfig: {
                    title: '标题',
                    value: '标题',
                    place: '请输入标题',
                    max: 10
                },
                linkConfig: {
                    title: '链接',
                    value: '',
                    place: '请输入链接地址',
                    max: 100
                },
                bgColor: {
                    title: '背景颜色(渐变)',
                    name: 'bgColor',
                    default: [
                        {
                            item: '#F62C2C'
                        },
                        {
                            item: '#F96E29'
                        }
                    ],
                    color: [
                        {
                            item: '#F62C2C'
                        },
                        {
                            item: '#F96E29'
                        }
                    ]
                },
                textColor: {
                    title: '字体颜色',
                    name: 'textColor',
                    default: [
                        {
                            item: '#fff'
                        }
                    ],
                    color: [
                        {
                            item: '#fff'
                        }
                    ]
                },
                textStyle: {
                    title: '文本样式',
                    type: 0,
                    list: [
                        {
                            val: '正常',
                            style: 'normal',
                            icon: 'icondoc_general'
                        },
                        {
                            val: '斜体',
                            style: 'italic',
                            icon: 'icondoc_skew'
                        },
                        {
                            val: '加粗',
                            style: 'bold',
                            icon: 'icondoc_bold'
                        }
                    ]
                },
                boxStyle: {
                    title: '边框样式',
                    name: 'boxStyle',
                    type: 0,
                    list: [
                        {
                            val: '圆角',
                            icon:'iconPic_fillet'
                        },
                        {
                            val: '直角',
                            icon:'iconPic_square'
                        }
                    ]
                },
                txtStyle: {
                    title: '文本位置',
                    name: 'txtStyle',
                    type: 0,
                    list: [
                        {
                            val: '居左',
                            icon:'icondoc_left'
                        },
                        {
                            val: '居中',
                            icon:'icondoc_center'
                        },
                        {
                            val: '居右',
                            icon:'icondoc_right'
                        }
                    ]
                },
                prConfig: {
                    title: '背景边距',
                    val: 10,
                    min: 0
                },
                // 页面间距
                mbConfig: {
                    title: '页面间距',
                    val: 0,
                    min: 0
                },
                hotWords: {
                    list: [
                        {
                            val: ''
                        }
                    ]
                },
                logoConfig:{
                    type: 1,
                    header: '设置logo',
                    title: '',
                    url: ''
                }
            },
            bgColor: [],
            rollStyle: '',
            txtPosition: '',
            slider: '',
            pageData: {},
            prConfig:0,
            logoUrl:'',
            tabVal:-1,
            radioVal:-1,
            textColor:'',
            textStyle:'',
            titleConfig:''
        }
    },
    mounted () {
        this.$nextTick(() => {
            this.pageData = this.$store.state.admin.mobildConfig.defaultArray[this.num]
            this.setConfig(this.pageData)
        })
    },
    methods: {
        setConfig (data) {
            if(!data) return
            if(data.mbConfig){
                console.log(data)
                this.bgColor = data.bgColor.color;
                this.rollStyle = data.boxStyle.type;
                this.txtPosition = data.txtStyle.type;
                this.slider = data.mbConfig.val;
                this.logoUrl = data.logoConfig.url;
                this.prConfig = data.prConfig.val;
                this.tabVal = data.tabConfig.tabVal;
                this.radioVal = data.radioConfig.tabVal;
                this.textColor = data.textColor.color[0].item;
                this.textStyle = data.textStyle.list[data.textStyle.type].style;
                this.titleConfig = data.titleConfig.value;
            }
        }
    }
}
