import cardsData from '@/components/cards/cards';
import { mapState } from 'vuex';
import { formatDate } from '@/utils/validate';
import { combineListApi, orderPinkListApi, statisticsApi } from '@/api/marketing';
import timeOptions from "@/utils/timeOptions";
export default {
    name: 'combinalist',
    filters: {
        formatDate (time) {
            if (time !== 0) {
                let date = new Date(time * 1000);
                return formatDate(date, 'yyyy-MM-dd hh:mm');
            }
        }
    },
    components: { cardsData },
    data () {
        return {
            cardLists: [],
            modals: false,
            options: timeOptions,
            grid: {
                xl: 7,
                lg: 10,
                md: 12,
                sm: 12,
                xs: 24
            },
            loading: false,
            formValidate: {
                status: '',
                data: '',
                page: 1,
                limit: 15
            },
            columns1: [
                {
                    title: '头像',
                    slot: 'avatar',
                    minWidth: 150
                },
                {
                    title: '开团团长',
                    slot: 'nickname',
                    minWidth: 170
                },
                {
                    title: '开团时间',
                    slot: 'add_time',
                    minWidth: 150
                },
                {
                    title: '拼团商品',
                    slot: 'title',
                    minWidth: 400
                },
                {
                    title: '几人团',
                    key: 'people',
                    minWidth: 120
                },
                {
                    title: '几人参加',
                    key: 'count_people',
                    minWidth: 100
                },
                {
                    title: '结束时间',
                    slot: 'stop_time',
                    minWidth: 150
                },
                {
                    title: '状态',
                    slot: 'status',
                    minWidth: 100
                },
                {
                    title: '操作',
                    slot: 'action',
                    fixed: 'right',
                    minWidth: 170
                }
            ],
            tableList: [],
            total: 0,
            timeVal: [],
            loading2: false,
            tabList3: [],
            columns2: [
                {
                    title: 'ID',
                    key: 'id',
                    width: 80
                },
                {
                    title: '用户名称',
                    slot: 'nickname',
                    minWidth: 150
                },
                {
                    title: '用户头像',
                    slot: 'avatar'
                },
                {
                    title: '订单编号',
                    key: 'order_id'
                },
                {
                    title: '金额',
                    key: 'price'
                },
                {
                    title: '订单状态',
                    slot: 'action'
                }
            ],
            rows: {}
        }
    },
    computed: {
        ...mapState('admin/layout', [
            'isMobile'
        ]),
        labelWidth () {
            return this.isMobile ? undefined : 96;
        },
        labelPosition () {
            return this.isMobile ? 'top' : 'right';
        }
    },
    created () {
        this.getList();
        this.getStatistics();
    },
    methods: {
        // 拼团统计
        getStatistics () {
            statisticsApi().then(async res => {
                let data = res.data
                this.cardLists = data.res;
            }).catch(res => {
                this.$Message.error(res.msg);
            })
        },
        // 查看详情
        Info (row) {
            this.modals = true;
            this.rows = row;
            orderPinkListApi(row.id).then(async res => {
                let data = res.data
                this.tabList3 = data.list;
                this.loading = false;
            }).catch(res => {
                this.loading = false;
                this.$Message.error(res.msg);
            })
        },
        // 具体日期
        onchangeTime (e) {
            this.timeVal = e
            if(this.timeVal[0]===''){
                this.formValidate.data = '';
            }else {
                this.formValidate.data = this.timeVal.join('-');
            }
            this.formValidate.page = 1;
        },
        // 选择时间
        selectChange (tab) {
            this.formValidate.page = 1;
            this.formValidate.data = tab;
            this.timeVal = [];
            this.getList();
        },
        // 列表
        getList () {
            this.loading = true;
            this.formValidate.status = this.formValidate.status || '';
            combineListApi(this.formValidate).then(async res => {
                let data = res.data
                this.tableList = data.list;
                this.total = res.data.count;
                this.loading = false;
            }).catch(res => {
                this.loading = false;
                this.$Message.error(res.msg);
            })
        },
        pageChange (index) {
            this.formValidate.page = index;
            this.getList();
        },
        // 表格搜索
        userSearchs () {
            this.formValidate.page = 1;
            this.getList();
        }
    }
}
