import { mapState } from 'vuex';
import groupFrom from './components/groupFrom';
import { groupListApi } from '@/api/system';
export default {
    name: 'group',
    components: { groupFrom },
    data () {
        return {
            grid: {
                xl: 7,
                lg: 7,
                md: 12,
                sm: 24,
                xs: 24
            },
            formValidate: {
                page: 1,
                limit: 20,
                title: ''
            },
            loading: false,
            tabList: [],
            total: 0,
            columns1: [
                {
                    title: 'ID',
                    key: 'id',
                    width: 80
                },
                {
                    title: 'KEY',
                    key: 'config_name',
                    minWidth: 130
                },
                {
                    title: '数据组名称',
                    key: 'name',
                    minWidth: 130
                },
                {
                    title: '简介',
                    key: 'info',
                    minWidth: 130
                },
                {
                    title: '操作',
                    slot: 'action',
                    fixed: 'right',
                    minWidth: 150
                }
            ],
            FromData: null,
            titleFrom: '',
            groupId: 0,
            addId: ''
        }
    },
    computed: {
        ...mapState('admin/layout', [
            'isMobile'
        ]),
        labelWidth () {
            return this.isMobile ? undefined : 75;
        },
        labelPosition () {
            return this.isMobile ? 'top' : 'right';
        }
    },
    mounted () {
        this.getList();
    },
    methods: {
        // 跳转到组合数据列表页面
        goList (row) {
            this.$router.push({ path: '/admin/system/config/system_group/list/' + row.id });
        },
        // 列表
        getList () {
            this.loading = true;
            groupListApi(this.formValidate).then(async res => {
                let data = res.data
                this.tabList = data.list;
                this.total = data.count;
                this.loading = false;
            }).catch(res => {
                this.loading = false;
                this.$Message.error(res.msg);
            })
        },
        pageChange (index) {
            this.formValidate.page = index;
            this.getList();
        },
        // 表格搜索
        userSearchs () {
            this.formValidate.page = 1;
            this.getList();
        },
        // 点击添加
        groupAdd (title) {
            this.$refs.groupfroms.modals = true;
            this.titleFrom = title;
            this.addId = 'addId';
        },
        // 删除
        del (row, tit, num) {
            let delfromData = {
                title: tit,
                num: num,
                url: `setting/group/${row.id}`,
                method: 'DELETE',
                ids: ''
            };
            this.$modalSure(delfromData).then((res) => {
                this.$Message.success(res.msg);
                this.tabList.splice(num, 1);
            }).catch(res => {
                this.$Message.error(res.msg);
            });
        },
        // 编辑
        edit (row, title) {
            this.titleFrom = title;
            this.groupId = row.id;
            this.$refs.groupfroms.fromData(row.id);
            this.$refs.groupfroms.modals = true;
            this.addId = ''
        }
    }
}
