    import { mapState } from 'vuex';
    import { productListApi, productCreateApi, productEditApi, setShowApi, treeListApi } from '@/api/product';
    import editFrom from '../../../components/from/from';
    export default {
        name: 'product_productClassify',
        components: {
            editFrom
        },
        data () {
            return {
                treeSelect: [],
                FromData: null,
                grid: {
                    xl: 7,
                    lg: 7,
                    md: 12,
                    sm: 24,
                    xs: 24
                },
                loading: false,
                artFrom: {
                    pid: 0,
                    is_show: '',
                    page: 1,
                    cate_name: '',
                    limit: 15,
                    id:0
                },
                total: 0,
                tableData: []
            }
        },
        computed: {
            ...mapState('admin/layout', [
                'isMobile'
            ]),
            ...mapState('admin/userLevel', [
                'categoryId'
            ]),
            labelWidth () {
                return this.isMobile ? undefined : 96;
            },
            labelPosition () {
                return this.isMobile ? 'top' : 'right';
            }
        },
        mounted () {
            this.goodsCategory();
            this.getList();
        },
        methods: {
            // 商品分类；
            goodsCategory () {
                treeListApi(0).then(res => {
                    this.treeSelect = res.data;
                }).catch(res => {
                    this.$Message.error(res.msg);
                })
            },
            // 列表
            getList () {
                this.loading = true;
                this.artFrom.is_show = this.artFrom.is_show || '';
                productListApi(this.artFrom).then(async res => {
                    let data = res.data
                    this.tableData = data.list;
                    this.total = data.count;
                    this.loading = false;
                }).catch(res => {
                    this.loading = false;
                    this.$Message.error(res.msg);
                })
            },
            loadChildrenMethod ({row}){
                console.log(row.id);
                this.artFrom.pid = row.id;
                return new Promise((resolve, reject) => {
                    productListApi(this.artFrom).then(res=>{
                        let arr = res.data.list;
                        resolve(arr);
                    })
                })
            },
            pageChange (index) {
                this.artFrom.page = index;
                this.getList();
            },
            // 添加
            addClass () {
                this.$modalForm(productCreateApi()).then(() => {
                    this.artFrom.pid = 0;
                    this.getList();
                    this.goodsCategory();
                }
                );
            },
            // 编辑
            edit (row) {
                this.$modalForm(productEditApi(row.id)).then(() => {
                    this.artFrom.pid = 0;
                    this.getList();
                    this.goodsCategory();
                    //this.findSupId(this.tableData,row.id,res);
				});
            },
						findSupId(data,supplierId,res){
							var fun = function (i,n) {
								if(i && i.length > 0){
									for(let v in i){
										if(i[v].id==n){
											 i[v].cate_name = res.data.cate_name
											 i[v].is_show = res.data.is_show
											 i[v].sort = res.data.sort
											 i[v].pic = res.data.pic
											 return
										}else{
											if(i[v].children && i[v].children.length > 0){
												fun(i[v].children, n)
											}
										}
									}
								}
							}
							fun(data,supplierId);
						},

            // 修改状态
            onchangeIsShow (row) {
                let data = {
                    id: row.id,
                    is_show: row.is_show
                }
                setShowApi(data).then(async res => {
                    this.$Message.success(res.msg);
                    this.artFrom.pid = 0;
                    this.getList()
                }).catch(res => {
                    this.$Message.error(res.msg);
                })
            },
            // 下拉树
            handleCheckChange (data) {
                let value = ''
                let title = ''
                this.list = []
                this.artFrom.pid = 0;
                data.forEach((item, index) => {
                    value += `${item.id},`
                    title += `${item.title},`
                });
                value = value.substring(0, value.length - 1)
                title = title.substring(0, title.length - 1)
                this.list.push({
                    value,
                    title
                });
                this.artFrom.pid = value;
                this.getList();
            },
            // 删除
            del (row, tit, num) {
                let delfromData = {
                    title: tit,
                    num: num,
                    url: `product/category/${row.id}`,
                    method: 'DELETE',
                    ids: ''
                };
                this.$modalSure(delfromData).then((res) => {
                    this.$Message.success(res.msg);
                    this.artFrom.pid = 0;
                    this.getList();
                    this.goodsCategory();
                }).catch(res => {
                    this.$Message.error(res.msg);
                });
            },
            // 表格搜索
            userSearchs () {
                this.artFrom.pid = 0;
                this.artFrom.page = 1;
                this.getList();
            }
        }
    }
