import { mapState, mapActions } from 'vuex';

export default {
    name: 'baseHeader',
    props: {
        kefuInfo: {
            type: Object,
            default: function () {
                return {}
            }
        },
        online: {
            type: Boolean | Number,
            default: true
        }
    },
    computed: {
        ...mapState('admin/layout', [
            'logoutConfirm'
        ])
    },
    data () {
        return {
            menuList: [
                {
                    key: 0,
                    title: '客户信息'
                },
                {
                    key: 1,
                    title: '交易订单'
                },
                {
                    key: 2,
                    title: '商品信息'
                }
            ],
            curIndex: 0,
            isOnline: false
        }
    },
    mounted() {
        document.addEventListener('click',()=>{
            this.isOnline = false
        })
    },
    methods:{
        ...mapActions('admin/account', [
            'logout',
            'logoutKefu'
        ]),
        selectTab (item) {
            this.curIndex = item.key
            this.bus.$emit('selectRightMenu', this.curIndex)
        },
        setOnline () {
            this.isOnline = !this.isOnline
        },
        changeOnline (type) {
            this.$emit('setOnline', type);
            this.isOnline = false
        },
        // 退出登录
        outLogin(){
            let self = this
            this.$Modal.confirm({
                title: '退出登录确认',
                content: '您确定退出登录当前账户吗？打开的标签页和个人设置将会保存。',
                onOk: () => {
                    self.logoutKefu({
                        confirm: false,
                        vm: self
                    });
                },
                onCancel: () => {

                }
            });
        },
        // 搜索
        bindSearch(e){
            this.$emit('search', e.target.value);
        },
        // inputChange
        inputChange(e){
            this.bus.$emit('change',e.target.value)
        }
    }
}
