import { mapState } from 'vuex';
export default {
    name: 'home_coupon',
    cname: '优惠券',
    configName: 'c_home_coupon',
    icon: 'iconyouhuiquan1',
    type:1,// 0 基础组件 1 营销组件 2工具组件
    defaultName:'coupon', // 外面匹配名称
    props: {
        index: {
            type: null
        },
        num: {
            type: null
        }
    },
    computed: {
        ...mapState('admin/mobildConfig', ['defaultArray'])
    },
    watch: {
        pageData: {
            handler (nVal, oVal) {
                this.setConfig(nVal)
            },
            deep: true
        },
        num: {
            handler (nVal, oVal) {
                let data = this.$store.state.admin.mobildConfig.defaultArray[nVal]
                this.setConfig(data)
            },
            deep: true
        },
        'defaultArray': {
            handler (nVal, oVal) {
                let data = this.$store.state.admin.mobildConfig.defaultArray[this.num]
                this.setConfig(data);
            },
            deep: true
        }
    },
    data () {
        return {
            // 默认初始化数据禁止修改
            defaultConfig: {
                name: 'coupon',
                timestamp: this.num,
                bgColor: {
                    title: '背景颜色',
                    default: [
                        {
                            item: '#F8F8F8'
                        }

                    ],
                    color: [
                        {
                            item: '#F8F8F8'
                        }

                    ]
                },
                bgStyle: {
                    title: '背景样式',
                    name: 'bgStyle',
                    type: 0,
                    list: [
                        {
                            val: '直角',
                            icon: 'iconPic_square'
                        },
                        {
                            val: '圆角',
                            icon: 'iconPic_fillet'
                        }
                    ]
                },
                prConfig: {
                    title: '背景边距',
                    val: 0,
                    min: 0
                },
                themeColor: {
                    title: '主题颜色',
                    default: [
                        {
                            item: '#E93323'
                        }

                    ],
                    color: [
                        {
                            item: '#E93323'
                        }

                    ]
                },
                // 页面间距
                mbConfig: {
                    title: '页面间距',
                    val: 0,
                    min: 0
                }
            },
            pageData: {},
            bgColor: [],
            mTOP: 0,
            themeColor: [],
            bgStyle:0,
            prConfig:0
        }
    },
    mounted () {
        this.$nextTick(() => {
            this.pageData = this.$store.state.admin.mobildConfig.defaultArray[this.num]
            this.setConfig(this.pageData)
        })
    },
    methods: {
        setConfig (data) {
            if(!data) return
            if(data.mbConfig){
                this.bgColor = data.bgColor.color
                this.mTOP = data.mbConfig.val;
                this.themeColor = data.themeColor.color
                this.bgStyle = data.bgStyle.type;
                this.prConfig = data.prConfig.val;
            }
            // this.edge = data.lrConfig.val;
            // this.imgStyle = data.imgConfig.type
            // this.imgSrc = data.swiperConfig.list[0].img
        }
    }
}
