    import { mapState } from 'vuex';
    import {
        deliveryList,
        orderDeliveryAdd,
        orderDeliveryEdit,
        orderDeliveryStatus
    } from '@/api/order';

    export default {
        name: 'index',
        computed: {
            ...mapState('admin/layout', ['isMobile']),
            // ...mapState('admin/userLevel', ['categoryId'])
        },
        data () {
            return {
                columns1: [
                    {
                        title: 'ID',
                        key: 'id',
                        width: 80
                    },
                    {
                        title: '微信名称',
                        key: 'nickname',
                        minWidth: 120
                    },
                    {
                        title: '头像',
                        slot: 'avatar',
                        minWidth: 60
                    },
                    {
                        title: '名称',
                        key: 'wx_name',
                        minWidth: 120
                    },
                    {
                        title: '手机号码',
                        key: 'phone',
                        minWidth: 120
                    },
                    {
                        title: '是否显示',
                        slot: 'status',
                        minWidth: 120
                    },
                    {
                        title: '添加时间',
                        key: 'add_time',
                        minWidth: 120
                    },
                    {
                        title: '操作',
                        slot: 'action',
                        fixed: 'right',
                        minWidth: 150
                    }
                ],
                data1: [],
                total: 0,
                tableOptions: {
                    page: 1,
                    limit: 15
                },
                loading: false
            };
        },
        created () {
            this.getOrderDeliveryList();
        },
        methods: {
            // 配送员列表
            getOrderDeliveryList () {
                this.loading = true;
                deliveryList(this.tableOptions)
                    .then((res) => {
                        this.data1 = res.data.list;
												this.total = res.data.count;
                        this.loading = false;
                    })
                    .catch((err) => {
                        this.loading = false;
                        this.$Message.error(err.msg);
                });
            },
            // 添加配送员
            add () {
                this.$modalForm(orderDeliveryAdd()).then(() => this.getOrderDeliveryList());
            },
            // 编辑
            edit (row) {
                this.$modalForm(orderDeliveryEdit(row.id)).then(() => this.getOrderDeliveryList());
            },
            // 删除
            del (row, tit, num) {
                let delfromData = {
                    title: tit,
                    num: num,
                    url: `/order/delivery/del/${row.id}`,
                    method: 'DELETE',
                    ids: ''
                };
                this.$modalSure(delfromData)
                    .then((res) => {
                        this.$Message.success(res.msg);
                        this.data1.splice(num, 1);
                    })
                    .catch((res) => {
                        this.$Message.error(res.msg);
                });
            },
            // 是否显示
            onchangeIsShow (row) {
                orderDeliveryStatus(row)
                    .then((res) => {
                        this.$Message.success(res.msg);
                    })
                    .catch((err) => {
                        this.$Message.error(err.msg);
                });
            },
            pageChange (index) {
                this.tableOptions.page = index;
                this.getOrderDeliveryList();
            }
        }
    };
