import {mapState} from "vuex";

export default {
    name: "index.vue",
    props:{
        configObj:{
            type:Object,
            default:function () {
                return {}
            }
        },
        configNme:{
            type:String,
            default:''
        }
    },
    data(){
        return {
            status:true,
            txtColor:'',
            activeTxtColor:'',
            bgColor:'',
            menuList:[],
            isSpecial:2
        }
    },
    computed: {
        ...mapState('admin/mobildConfig', ['pageFooter'])
    },
    watch:{
        'pageFooter': {
            handler (nVal, oVal) {
                this.setConfig(nVal);
            },
            deep: true
        }
    },
    mounted() {
        let data = this.$store.state.admin.mobildConfig.pageFooter
        this.setConfig(data)
    },
    methods:{
        setConfig (data) {
            this.status = data.status
            this.txtColor = data.txtColor.color[0].item;
            this.activeTxtColor = data.activeTxtColor.color[0].item;
            this.bgColor = data.bgColor.color[0].item;
            this.menuList = []
            this.$set(this,'menuList',data.menuList)
            if(data.status.title == '是否显示'){
                this.isSpecial = 2
            }else {
                this.isSpecial = 0
            }
        }
    }
}
