import { mapState } from "vuex";
import { colorChange, getColorChange } from "@/api/diy";
export default {
    name: "themeStyle",
    data() {
        return {
            grid: {
                xl: 7,
                lg: 7,
                md: 12,
                sm: 24,
                xs: 24,
            },
            picList:[],
            picListBule:[
                {image:require('@/assets/images/bule.jpg')}
            ],
            picListGreen:[
                {image:require('@/assets/images/green.jpg')}
            ],
            picListRed:[
                {image:require('@/assets/images/red.jpg')}
            ],
            picListPink:[
                {image:require('@/assets/images/pink.jpg')}
            ],
            picListOrange:[
                {image:require('@/assets/images/orange.jpg')}
            ],
            current:'',
            clientHeight:0,
            loadingExist:false
        };
    },
    computed: {
        ...mapState("admin/layout", ["isMobile","menuCollapse"]),
        labelWidth() {
            return this.isMobile ? undefined : 100;
        },
        labelPosition() {
            return this.isMobile ? "top" : "right";
        }
    },
    created() {
        this.picList = this.picListBule;
        this.getInfo();
    },
    mounted: function() {
        this.$nextTick(()=>{
            this.clientHeight = `${document.documentElement.clientHeight}`-250;//获取浏览器可视区域高度
            let that = this;
            window.onresize = function(){
                that.clientHeight =  `${document.documentElement.clientHeight}`-250;
            }
        })
    },
    methods: {
        getInfo(){
            getColorChange('color_change').then(res=>{
                this.current = res.data.status?res.data.status:3
                this.changeColor(this.current);
            }).catch((err)=>{
                this.$Message.error(err.msg);
            })
        },
        submit(){
            this.loadingExist = true
            colorChange(this.current,'color_change').then(res=>{
                this.loadingExist = false
                this.$Message.success(res.msg);
            }).catch(()=>{
                this.loadingExist = false
            })
        },
        changeColor(e){
            switch(e){
                case 1:
                    this.picList = this.picListBule;
                    break;
                case 2:
                    this.picList = this.picListGreen;
                    break;
                case 3:
                    this.picList = this.picListRed;
                    break;
                case 4:
                    this.picList = this.picListPink;
                    break;
                case 5:
                    this.picList = this.picListOrange;
                    break;
                default:
                    break
            }
        }
    },
};
