import "emoji-awesome/dist/css/google.min.css";
import emojiList from "@/utils/emoji";
import { Socket } from "@/libs/socket";
import Setting from "@/setting";
import Cookies from "js-cookie";
import { chatListApi, serviceListApi, getAdvApi } from "@/api/kefu";
import feedBack from "./feedback";
const chunk = function (arr, num) {
  num = num * 1 || 1;
  var ret = [];
  arr.forEach(function (item, i) {
    if (i % num === 0) {
      ret.push([]);
    }
    ret[ret.length - 1].push(item);
  });
  return ret;
};
export default {
  name: "ChatRoom",
  auth: false,
  components: {
    feedBack,
  },
  props: {
    chatOptions: {
      type: Object,
      default: function () {
        return {
          show: false,
        };
      },
    },
  },
  directives: {
    drag: {
      inserted: function (el) {
        let x = 0;
        let y = 0;
        let l = 0;
        let t = 0;
        let isDown = false;
        el.onmousedown = function (e) {
          x = e.clientX;
          y = e.clientY;
          l = el.parentNode.offsetLeft;
          t = el.parentNode.offsetTop;
          isDown = true;
          el.style.cursor = "move";
          window.onmousemove = function (e) {
            if (isDown == false) {
              return;
            }
            let nx = e.clientX;
            let ny = e.clientY;
            let nl = nx - (x - l);
            let nt = ny - (y - t);
            el.parentNode.style.left = nl + "px";
            el.parentNode.style.top = nt + "px";
          };
          window.onmouseup = function () {
            isDown = false;
            el.style.cursor = "default";
            window.onmousemove = null;
            window.onmouseup = null;
          };
          return false;
        };
      },
    },
  },
  data() {
    return {
      locations: `${location.origin}`,
      change: false,
      emojiGroup: chunk(emojiList, 20), // 表情列表
      emojiList: emojiList,
      emojiShow: false,
      recordList: [],
      limit: 20,
      loading: false,
      finished: false,
      chatCont: "",
      service: null,
      serviceData: {},
      uploadAction: "",
      notice: "",
      audio: null,
      muted: false,
      audioSrc: "",
      upperId: 0,
      uploadData: {},
      is_tourist: 1, // 0登录状态，1未登录状态游客
      text: "",
      isLoad: false,
      page: 1,
      tourist_avatar: "", // 游客头像
      tourist_uid: "", // 游客id
      toUid: "", // 客服id
      kufuToken: "", // token
      pageWs: "",
    };
  },
  watch: {
    muted(value) {
      this.audio.muted = value;
    },
  },
  computed: {
    records() {
      return this.recordList.map((item, index) => {
        if (index) {
          if (
            new Date(item.add_time) -
              new Date(this.recordList[index - 1].add_time) >=
            300000
          ) {
            item.show = true;
          } else {
            item.show = false;
          }
        } else {
          item.show = false;
        }
        return item;
      });
    },
  },
  created() {
    if (location.href.indexOf("kefu") != -1)
      this.uploadAction =
        Setting.apiBaseURL.replace(/adminapi/, "kefuapi") + "/tourist/upload";
    let token = Cookies.get("auth._token.local1");
    this.kufuToken = token ? token.split("Bearer%20")[1] : "";
  },
  mounted() {
    let that = this;
    window.addEventListener("click", function () {
      if (that.emojiShow) {
        that.emojiShow = false;
      }
    });
    if (this.$wechat._isMobile())
      this.$router.replace("/kefu/mobile_user_chat");
    this.getService();
    this.pageWs = Socket(true, this.kufuToken, this.tourist_uid);
    this.text = this.replace_em("[em-smiling_imp]");
    this.wsStart();
  },
  beforeDestroy() {
    this.pageWs.close();
  },
  methods: {
    wsStart() {
      let that = this;
      this.pageWs
        .then((ws) => {
          if (this.kufuToken) {
            ws.send({
              type: "login",
              data: this.kufuToken,
            });
          }
          this.getService();
          ws.$on(["reply", "chat"], (data) => {
            if (data.msn_type == 1) {
              data.msn = this.replace_em(data.msn);
            }
            this.recordList.push(data);
            setTimeout((res) => {
              this.$nextTick(function () {
                this.$refs.record.scrollTop =
                  this.$refs.record.scrollHeight -
                  this.$refs.record.clientHeight;
              });
            }, 300);
          });
          // 监听客服转接
          ws.$on("to_transfer", (data) => {
            this.toUid = data.toUid;
            ws.send({
              data: {
                id: this.toUid,
              },
              type: "to_chat",
            });
          });
          ws.$on("socket_error", () => {
            this.$Message.error("连接失败");
          });
          ws.$on("err_tip", (data) => {
            this.$Message.error(data.msg);
          });
          ws.$on("success", (data) => {
            this.is_tourist = 0;
          });
          ws.$on("timeout", (data) => {
            setTimeout(() => {
              this.wsRestart();
            }, 2000);
          });
        })
        .catch((error) => {
          setTimeout((res) => {
            this.wsRestart();
          }, 2000);
        });
    },
    wsRestart() {
      this.pageWs = Socket(true, this.kufuToken, this.tourist_uid);
      this.wsStart();
    },
    onLook(id) {
      window.open(`${location.origin}/home/goods_detail/${id}`);
    },
    // 关闭
    closeChange(msg) {
      this.change = msg;
    },
    // 统一发送处理
    sendMsg(msn, type) {
      let obj = {
        type: "chat",
        data: {
          msn,
          type,
          is_tourist: this.is_tourist,
          to_uid: this.toUid,
          tourist_uid: this.tourist_uid,
          tourist_avatar: this.tourist_avatar,
          form_type: this.$wechat.isWeixin() ? 1 : 3,
        },
      };
      this.pageWs.then((ws) => {
        ws.send(obj);
      });
    },
    // 随机客服
    getService() {
      serviceListApi({ token: this.kufuToken || "" })
        .then((res) => {
          this.serviceData = res.data;
          this.upperId = 0;
          this.toUid = res.data.uid;
          this.tourist_uid = res.data.tourist_uid;
          this.tourist_avatar = res.data.tourist_avatar;
          let obj = {
            data: {
              id: res.data.uid,
              tourist_uid: this.tourist_uid,
            },
            type: "to_chat",
          };
          this.pageWs.then((ws) => {
            ws.send(obj);
          });
          if (this.kufuToken) {
            this.getRecordList();
          }
        })
        .catch((err) => {
          // this.$Message.error(err.msg);
          this.change = true;
        });
    },
    roomClick(event) {
      // if (
      //     !event.target.classList.contains("emoji-panel") &&
      //     !event.target.classList.contains("emoji-btn") &&
      //     !event.target.classList.contains("icon-biaoqing") &&
      //     this.emojiShow
      // ) {
      //     this.emojiShow = false;
      // }
    },
    // enter 发送
    ctrlEnter(e) {
      if (e.keyCode == 13) {
        e.preventDefault();
      }
      if (this.chatCont.trim()) {
        this.sendMessage();
      }
    },
    // 关闭聊天窗口
    close() {
      this.$emit("chat-close");
    },
    // 选择表情
    selectEmoji(data) {
      let val = `[${data}]`;
      this.chatCont += val;
      this.emojiShow = false;
    },
    // 聊天表情转换
    replace_em(str) {
      str = str.replace(/\[em-([\s\S]*)\]/g, "<span class='em em-$1'/></span>");
      return str;
    },
    onScroll(event) {
      if (event.target.scrollTop <= 30) {
        if (this.kufuToken) {
          this.getRecordList();
        }
      }
    },
    // 聊天记录
    getRecordList() {
      if (this.loading) {
        return;
      }
      if (this.finished) {
        return;
      }
      this.loading = true;
      chatListApi({
        uid: this.serviceData.uid,
        limit: this.limit,
        upperId: this.upperId,
        token: this.kufuToken,
      })
        .then((res) => {
          if (res.data.length === 0) return (this.loading = false);
          res.data.forEach((el) => {
            if (el.msn_type == 1) {
              el.msn = this.replace_em(el.msn);
            }
          });
          let selector = "";
          if (this.upperId == 0) {
            selector = `chat_${res.data[res.data.length - 1].id}`;
          } else {
            selector = `chat_${this.recordList[0].id}`;
          }

          this.recordList = [...res.data, ...this.recordList];
          this.upperId = res.data.length > 0 ? res.data[0].id : 0;
          this.loading = false;
          this.finished = res.data.length < this.limit;
          this.$nextTick(function () {
            this.setPageScrollTo(selector);
          });
        })
        .catch((err) => {
          this.$Message.error(err.msg);
        });
    },
    // 设置页面滚动位置
    setPageScrollTo(selector) {
      this.$nextTick(() => {
        if (selector) {
          setTimeout(() => {
            let num =
              parseFloat(document.getElementById(selector).offsetTop) - 60;
            this.$refs.record.scrollTop = num;
          }, 0);
        } else {
          var container = document.querySelector("#chat_scroll");
          this.$refs.record.scrollTop = container.offsetHeight;
          setTimeout((res) => {
            if (
              this.$refs.record.scrollTop != this.$refs.scrollBox.offsetHeight
            ) {
              this.$refs.record.scrollTop = document.querySelector(
                "#chat_scroll"
              ).offsetHeight;
            }
          }, 300);
        }
      });
    },
    // 表情包显示隐藏
    emojiSwitch() {
      this.emojiShow = true;
    },
    // 发送消息
    sendMessage() {
      this.sendMsg(this.chatCont, 1);
      this.chatCont = "";
    },
    chat(data) {
      if (data.uid != this.$auth.user.uid && this.audio) {
        this.audio.play();
      }
      this.recordList.push(data);
      this.$nextTick(() => {
        this.$refs.record.scrollTop =
          this.$refs.record.scrollHeight - this.$refs.record.clientHeight;
      });
    },
    sendGoods() {
      if (this.chatOptions.goodsId) {
        this.pageWs.then((ws) => {
          ws.send({
            data: {
              msn: this.chatOptions.goodsId,
              type: 5,
              to_uid: this.toUid,
            },
            type: "to_chat",
          });
        });
      }
    },
    sendOrder() {
      if (this.chatOptions.orderId) {
        this.pageWs.then((ws) => {
          ws.send({
            data: {
              msn: this.chatOptions.orderId,
              type: 6,
              to_uid: this.toUid,
            },
            type: "to_chat",
          });
        });
      }
    },
    chatEnd() {
      if (navigator.userAgent.indexOf("MSIE") > 0) {
        if (navigator.userAgent.indexOf("MSIE 6.0") > 0) {
          window.opener = null;
          window.close();
        } else {
          window.open("", "_top");
          window.top.close();
        }
      } else if (navigator.userAgent.indexOf("Firefox") > 0) {
        window.location.href = "about:blank ";
      } else {
        window.opener = null;
        window.open("", "_self", "");
        window.close();
      }
    },
    // 广告
    getNotice() {
      getAdvApi().then((res) => {
        this.notice = res.data.content;
      });
    },
    beforeUpload(file) {
      this.uploadData = {
        filename: file,
        token: this.kufuToken,
      };
      let promise = new Promise((resolve) => {
        this.$nextTick(function () {
          resolve(true);
        });
      });
      return promise;
    },
    handleFormatError(file) {
      this.$Message.error("上传图片只能是 jpg、jpg、jpeg、gif 格式!");
    },
    uploadSuccess(res) {
      this.sendMsg(res.data.url, 3);
    },
    uploadError(error) {
      this.$message.error(error);
    },
  },
};
